#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <errno.h>
#include <string.h>
#include <unistd.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/520.txt", "a");
    if (!f)
        return;

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int metal_shmem_open(const char *name, size_t size,
                     struct metal_io_region **result)
{
    struct metal_page_size *ps;
    int fd, error;

    probe_log("ENTER metal_shmem_open: name='%s', size=%zu", name ? name : "(null)", size);

    error = metal_shmem_open_generic(name, size, result);
    probe_log("metal_shmem_open_generic returned %d", error);
    if (!error) {
        probe_log("metal_shmem_open_generic succeeded, returning %d", error);
        return error;
    }

    error = metal_open(name, 1);
    probe_log("metal_open('%s', 1) returned %d", name ? name : "(null)", error);
    if (error < 0) {
        /* Log state just before calling metal_log so the invocation and context
         * of the potentially extension-using macro are recorded. */
        probe_log("About to call metal_log for failed open: name='%s', errno=%d (%s)",
                  name ? name : "(null)", errno, strerror(errno));

        metal_log(METAL_LOG_ERROR, "Failed to open shmem file :%s\n", name);

        probe_log("metal_log was called (METAL_LOG_ERROR) for name='%s', returning %d",
                  name ? name : "(null)", error);
        return error;
    }
    fd = error;
    probe_log("got fd=%d", fd);

    /* Iterate through page sizes in decreasing order. */
    metal_for_each_page_size_down(ps) {
        probe_log("loop: page_size=%zu, threshold=2*size=%zu", ps->page_size, (size_t)2 * size);
        if (ps->page_size > 2 * size) {
            probe_log("skipping page size %zu (too large)", ps->page_size);
            continue;
        }
        probe_log("trying metal_shmem_try_map with page_size=%zu, fd=%d, size=%zu",
                  ps->page_size, fd, size);
        error = metal_shmem_try_map(ps, fd, size, result);
        probe_log("metal_shmem_try_map returned %d", error);
        if (!error) {
            probe_log("metal_shmem_try_map succeeded for page_size=%zu", ps->page_size);
            break;
        }
    }

    close(fd);
    probe_log("closed fd=%d, returning error=%d", fd, error);
    return error;
}
