#include <stdio.h>
#include <unistd.h>

int metal_shmem_open(const char *name, size_t size,
                     struct metal_io_region **result)
{
    struct metal_page_size *ps;
    int fd, error;
    FILE *log = fopen("/tmp/logs/527.txt", "a");

    if (log) {
        fprintf(log, "metal_shmem_open START: name=%s, size=%zu, result=%p\n",
                name ? name : "(null)", size, (void *)result);
        fflush(log);
    }

    error = metal_shmem_open_generic(name, size, result);
    if (log) fprintf(log, "after metal_shmem_open_generic: error=%d\n", error);

    if (!error) {
        if (log) {
            fprintf(log, "early return (already opened): error=%d\n", error);
            fclose(log);
        }
        return error;
    }

    error = metal_open(name, 1);
    if (log) fprintf(log, "after metal_open: error=%d\n", error);

    if (error < 0) {
        if (log) {
            fprintf(log, "Failed to open shmem file: %s, error=%d\n",
                    name ? name : "(null)", error);
            fclose(log);
        }
        metal_log(METAL_LOG_ERROR, "Failed to open shmem file :%s\n", name);
        return error;
    }

    fd = error;
    if (log) {
        fprintf(log, "fd assigned: %d\n", fd);
        fflush(log);
    }

    /* Iterate through page sizes in decreasing order. */
    metal_for_each_page_size_down(ps) {
        if (log) fprintf(log, "loop page_size=%zu, requested size=%zu\n",
                         ps->page_size, size);

        if (ps->page_size > 2 * size) {
            if (log) fprintf(log, "skipping page size %zu (> 2*size)\n", ps->page_size);
            continue;
        }
        error = metal_shmem_try_map(ps, fd, size, result);
        if (log) fprintf(log, "after metal_shmem_try_map: error=%d\n", error);
        if (!error) {
            if (log) fprintf(log, "mapping succeeded with page_size=%zu\n", ps->page_size);
            break;
        }
    }

    if (log) {
        fprintf(log, "closing fd=%d, final error=%d\n", fd, error);
        fclose(log);
    }

    close(fd);
    return error;
}
