#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <string.h>
#include <stdlib.h>
#include <time.h>

/* Probe logging function: append records to /tmp/logs/530.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/530.txt", "a");
    if (!f)
        return;

    /* Timestamp for better traceability */
    time_t t = time(NULL);
    if (t != (time_t)-1) {
        struct tm tm_buf;
        if (localtime_r(&t, &tm_buf)) {
            char timestr[64];
            if (strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf))
                fprintf(f, "[%s] ", timestr);
        }
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                   struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;

    probe_log("enter metal_shmem_try_map: fd=%d, requested_size=%zu, page_size=%zu, page_shift=%d, mmap_flags=0x%x",
              fd, size, ps ? ps->page_size : 0, ps ? ps->page_shift : -1, ps ? ps->mmap_flags : 0);

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;

    probe_log("aligned size=%zu, pages=%zu", size, pages);

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (error) {
        probe_log("metal_map failed: fd=%d, size=%zu, mmap_flags=0x%x, error=%d (%s)",
                  fd, size, ps->mmap_flags, error, strerror(-error));
        metal_log(METAL_LOG_WARNING,
              "failed to mmap shmem %ld,0x%x - %s\n",
              size, ps->mmap_flags, strerror(-error));
        return error;
    }

    probe_log("metal_map succeeded: mem=%p", mem);

    error = mlock(mem, size);
    if (error) {
        probe_log("mlock failed: errno=%d (%s)", errno, strerror(errno));
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
              strerror(errno));
    } else {
        probe_log("mlock succeeded: mem=%p, size=%zu", mem, size);
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    if (!phys) {
        probe_log("malloc for phys failed: requested_bytes=%zu", phys_size);
        metal_unmap(mem, size);
        return -ENOMEM;
    }
    probe_log("allocated phys array: ptr=%p, bytes=%zu", (void *)phys, phys_size);

    io = malloc(sizeof(*io));
    if (!io) {
        probe_log("malloc for io failed: requested_bytes=%zu", sizeof(*io));
        free(phys);
        metal_unmap(mem, size);
        return -ENOMEM;
    }
    probe_log("allocated io region struct: ptr=%p", (void *)io);

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        probe_log("pagemap_fd < 0: using offset as PA. phys[0]=%llu", (unsigned long long)phys[0]);
        metal_log(METAL_LOG_WARNING,
        "shmem - failed to get va2pa mapping. use offset as pa.\n");
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        probe_log("pagemap_fd >= 0: performing virt->phys mappings for %zu pages", pages);
        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0) {
                phys[page] = METAL_BAD_OFFSET;
                probe_log("virt2phys failed for page=%zu, addr=%p, error=%d -> phys[%zu]=METAL_BAD_OFFSET",
                          page, virt + offset, error, page);
            } else {
                probe_log("virt2phys succeeded for page=%zu, addr=%p -> phys[%zu]=%llu",
                          page, virt + offset, page, (unsigned long long)phys[page]);
            }
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
            &metal_shmem_io_ops);
    }
    *result = io;

    probe_log("metal_shmem_try_map succeeded: io=%p, mem=%p, size=%zu, pages=%zu",
              (void *)io, mem, size, pages);

    return 0;
}
