suspicious_frags:
for_each_linux_driver(lbus, ldrv) {
		ret = metal_linux_probe_driver(lbus, ldrv);
		/* Clear the error if any driver is available */
		if (!ret)
			error = ret;
	}
metal_list_for_each(&irq_cntrs, node) {
		if (node == &cntr->node) {
			return 0;
		}
	}
for_each_linux_bus(lbus)
	valid += metal_linux_probe_bus(lbus) ? 0 : 1;
for (i = 0; i < count; i++) {
		int shift = metal_log2(sizes[i]);

		if ((shift & MAP_HUGE_MASK) != shift)
			continue;
		metal_add_page_size(
			hugetlbfs_find_path_for_size(sizes[i]),
			shift, (MAP_HUGETLB |
			(shift << MAP_HUGE_SHIFT)));
	}
