#include <stdio.h>
#include <errno.h>
#include <string.h>

/* Probe helper: append a structured log entry to /tmp/logs/370.txt.
 * Keeps original function logic unchanged; only observes state.
 */
static void probe_log_event(const char *event,
                            struct linux_device *ldev,
                            struct linux_driver *ldrv,
                            struct sysfs_attribute *attr,
                            int result)
{
    FILE *f = fopen("/tmp/logs/370.txt", "a");
    if (!f)
        return;

    fprintf(f, "EVENT: %s\n", event ? event : "(null)");

    if (ldev) {
        fprintf(f, "  ldev: %p\n", (void *)ldev);
        fprintf(f, "    dev_name: %s\n", ldev->dev_name ? ldev->dev_name : "(null)");
        if (ldev->sdev) {
            fprintf(f, "    sdev: %p\n", (void *)ldev->sdev);
            fprintf(f, "      driver_name: %s\n",
                    ldev->sdev->driver_name ? ldev->sdev->driver_name : "(null)");
        } else {
            fprintf(f, "    sdev: (null)\n");
        }
        fprintf(f, "    override: %p\n", (void *)ldev->override);
    } else {
        fprintf(f, "  ldev: (null)\n");
    }

    if (ldrv) {
        fprintf(f, "  ldrv: %p\n", (void *)ldrv);
        fprintf(f, "    drv_name: %s\n", ldrv->drv_name ? ldrv->drv_name : "(null)");
        fprintf(f, "    sdrv: %p\n", (void *)ldrv->sdrv);
    } else {
        fprintf(f, "  ldrv: (null)\n");
    }

    fprintf(f, "  attr: %p\n", (void *)attr);
    fprintf(f, "  result: %d\n", result);
    fprintf(f, "  errno: %d (%s)\n", errno, strerror(errno));
    fprintf(f, "----\n");
    fclose(f);
}

static int metal_uio_dev_bind(struct linux_device *ldev,
                  struct linux_driver *ldrv)
{
    struct sysfs_attribute *attr;
    int result;

    /* Probe: entry */
    probe_log_event("entry", ldev, ldrv, NULL, 0);

    if (strcmp(ldev->sdev->driver_name, ldrv->drv_name) == 0)
    {
        probe_log_event("already_bound_by_same_driver", ldev, ldrv, NULL, 0);
        return 0;
    }

    if (strcmp(ldev->sdev->driver_name, SYSFS_UNKNOWN) != 0) {
        probe_log_event("device_in_use_by_other_driver", ldev, ldrv, NULL, 0);
        metal_log(METAL_LOG_INFO, "device %s in use by driver %s\n",
                  ldev->dev_name, ldev->sdev->driver_name);
        return -EBUSY;
    }

    attr = sysfs_get_device_attr(ldev->sdev, "driver_override");
    if (!attr) {
        probe_log_event("no_override_attr", ldev, ldrv, NULL, 0);
        metal_log(METAL_LOG_ERROR, "device %s has no override\n",
                  ldev->dev_name);
        return -errno;
    }

    probe_log_event("have_override_attr", ldev, ldrv, attr, 0);

    result = sysfs_write_attribute(attr, ldrv->drv_name,
                                   strlen(ldrv->drv_name));
    if (result) {
        probe_log_event("failed_write_override", ldev, ldrv, attr, result);
        metal_log(METAL_LOG_ERROR, "failed to set override on %s\n",
                  ldev->dev_name);
        return -errno;
    }
    ldev->override = attr;

    attr = sysfs_get_driver_attr(ldrv->sdrv, "bind");
    if (!attr) {
        probe_log_event("no_driver_bind_attr", ldev, ldrv, NULL, 0);
        metal_log(METAL_LOG_ERROR, "driver %s has no bind\n", ldrv->drv_name);
        return -ENOTSUP;
    }

    probe_log_event("have_driver_bind_attr", ldev, ldrv, attr, 0);

    result = sysfs_write_attribute(attr, ldev->dev_name,
                                   strlen(ldev->dev_name));
    if (result) {
        probe_log_event("failed_write_bind", ldev, ldrv, attr, result);
        metal_log(METAL_LOG_ERROR, "failed to bind %s to %s\n",
                  ldev->dev_name, ldrv->drv_name);
        return -errno;
    }

    metal_log(METAL_LOG_DEBUG, "bound device %s to driver %s\n",
              ldev->dev_name, ldrv->drv_name);

    probe_log_event("bound_success", ldev, ldrv, attr, 0);

    return 0;
}
