#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>
#include <string.h>

/* Probe logging helper: append logs to /tmp/logs/390.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/390.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

static int metal_uio_dev_dma_map(struct linux_bus *lbus,
                 struct linux_device *ldev,
                 uint32_t dir,
                 struct metal_sg *sg_in,
                 int nents_in,
                 struct metal_sg *sg_out)
{
    int i, j;
    void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
    struct metal_io_region *io;

    (void)lbus;
    (void)dir;

    /* Log entry */
    probe_log("%s: enter, nents_in=%d, dev_name=%s\n",
              __func__, nents_in, (ldev && ldev->dev_name) ? ldev->dev_name : "(null)");

    /* Check if the the input virt address is MMIO address */
    for (i = 0; i < nents_in; i++) {
        vaddr_sg_lo = sg_in[i].virt;
        /* Original code performs pointer arithmetic on void* here. */
        vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;

        /* Log the computed sg range. Use uintptr_t for reproducible numeric logs. */
        probe_log("%s: sg[%d] virt_lo=0x%" PRIxPTR ", len=%u, virt_hi=0x%" PRIxPTR "\n",
                  __func__, i,
                  (uintptr_t)vaddr_sg_lo,
                  (unsigned)sg_in[i].len,
                  (uintptr_t)vaddr_sg_hi);

        for (j = 0, io = ldev->device.regions;
             j < (int)ldev->device.num_regions; j++, io++) {
            vaddr_lo = io->virt;
            /* Original code performs pointer arithmetic on void* here. */
            vaddr_hi = vaddr_lo + io->size;

            /* Log the region range being checked. */
            probe_log("%s: checking region[%d] virt_lo=0x%" PRIxPTR ", size=%u, virt_hi=0x%" PRIxPTR "\n",
                      __func__, j,
                      (uintptr_t)vaddr_lo,
                      (unsigned)io->size,
                      (uintptr_t)vaddr_hi);

            if (vaddr_sg_lo >= vaddr_lo &&
                vaddr_sg_hi <= vaddr_hi) {
                probe_log("%s: sg[%d] fits in region[%d]\n", __func__, i, j);
                break;
            }
        }
        if (j == (int)ldev->device.num_regions) {
            /* Log the failure case with the same details as the warning. */
            probe_log("%s: input address isn't MMIO addr: virt_lo=0x%" PRIxPTR ", len=%u (no matching region)\n",
                      __func__, (uintptr_t)vaddr_sg_lo, (unsigned)sg_in[i].len);

            metal_log(METAL_LOG_WARNING,
              "%s,%s: input address isn't MMIO addr: 0x%x,%d.\n",
            __func__, ldev->dev_name, vaddr_sg_lo, sg_in[i].len);
            return -EINVAL;
        }
    }
    if (sg_out != sg_in)
        memcpy(sg_out, sg_in, nents_in*(sizeof(struct metal_sg)));

    probe_log("%s: exit, returning %d\n", __func__, nents_in);
    return nents_in;
}
