#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <errno.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/393.txt */
static void metal_uio_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/393.txt", "a");
    if (f == NULL) {
        /* Best-effort logging; do not affect program flow if open fails */
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

static int metal_uio_dev_dma_map(struct linux_bus *lbus,
                 struct linux_device *ldev,
                 uint32_t dir,
                 struct metal_sg *sg_in,
                 int nents_in,
                 struct metal_sg *sg_out)
{
    int i, j;
    void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
    struct metal_io_region *io;

    /* Log entry and key parameters */
    metal_uio_log_probe("ENTER %s: lbus=%p ldev=%p dir=%u sg_in=%p nents_in=%d sg_out=%p",
                        __func__, (void *)lbus, (void *)ldev, (unsigned)dir,
                        (void *)sg_in, nents_in, (void *)sg_out);

    (void)lbus;
    (void)dir;

    /* Check if the the input virt address is MMIO address */
    for (i = 0; i < nents_in; i++) {
        vaddr_sg_lo = sg_in[i].virt;
        vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;

        metal_uio_log_probe("iter i=%d: sg_in[%d].virt=%p len=%u hi=%p",
                            i, i, vaddr_sg_lo, (unsigned)sg_in[i].len, vaddr_sg_hi);

        for (j = 0, io = ldev->device.regions;
             j < (int)ldev->device.num_regions; j++, io++) {
            vaddr_lo = io->virt;
            vaddr_hi = vaddr_lo + io->size;

            metal_uio_log_probe("  check region j=%d: io=%p lo=%p hi=%p size=%u",
                                j, (void *)io, vaddr_lo, vaddr_hi, (unsigned)io->size);

            if (vaddr_sg_lo >= vaddr_lo &&
                vaddr_sg_hi <= vaddr_hi) {
                metal_uio_log_probe("  matched region j=%d for sg_in[%d]", j, i);
                break;
            }
        }
        if (j == (int)ldev->device.num_regions) {
            /* Log the exact failure context before returning */
            metal_uio_log_probe(
              "WARN %s,%s: input address isn't MMIO addr: sg_in[%d].virt=%p len=%u; num_regions=%u",
              __func__, ldev->dev_name, i, vaddr_sg_lo, (unsigned)sg_in[i].len,
              (unsigned)ldev->device.num_regions);
            metal_uio_log_probe("EXIT %s: returning -EINVAL", __func__);
            return -EINVAL;
        }
    }

    if (sg_out != sg_in) {
        size_t bytes = (size_t)nents_in * (sizeof(struct metal_sg));
        metal_uio_log_probe("memcpy: copying %zu bytes from %p to %p", bytes, (void *)sg_in, (void *)sg_out);
        memcpy(sg_out, sg_in, nents_in * (sizeof(struct metal_sg)));
        metal_uio_log_probe("memcpy: completed");
    } else {
        metal_uio_log_probe("no memcpy: sg_out == sg_in (%p)", (void *)sg_in);
    }

    metal_uio_log_probe("EXIT %s: returning %d", __func__, nents_in);
    return nents_in;
}
