/* Probe helper: append formatted log lines to /tmp/logs/393.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/393.txt", "a");
    if (!f) {
        /* If opening fails, silently return to avoid changing behavior */
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

