static int metal_uio_dev_dma_map(struct linux_bus *lbus,
				 struct linux_device *ldev,
				 uint32_t dir,
				 struct metal_sg *sg_in,
				 int nents_in,
				 struct metal_sg *sg_out)
{
	int i, j;
	void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
	struct metal_io_region *io;
	FILE *fp = NULL;

	(void)lbus;
	(void)dir;

	/* Open probe log file. Append so repeated runs accumulate evidence. */
	fp = fopen("/tmp/logs/393.txt", "a");
	if (fp) {
		fprintf(fp, "ENTER: %s ldev=%p name=%s nents_in=%d\n",
			__func__, (void *)ldev, (ldev ? ldev->dev_name : "(null)"), nents_in);
	}

	/* Check if the the input virt address is MMIO address */
	for (i = 0; i < nents_in; i++) {
		vaddr_sg_lo = sg_in[i].virt;
		vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;
		if (fp) {
			fprintf(fp, "SG[%d]: virt=%p len=%lu -> [lo=%p hi=%p]\n",
				i, (void *)sg_in[i].virt, (unsigned long)sg_in[i].len,
				vaddr_sg_lo, vaddr_sg_hi);
		}
		for (j = 0, io = ldev->device.regions;
		     j < (int)ldev->device.num_regions; j++, io++) {
			vaddr_lo = io->virt;
			vaddr_hi = vaddr_lo + io->size;
			if (fp) {
				fprintf(fp, "  REGION[%d]: virt=%p size=%lu -> [lo=%p hi=%p]\n",
					j, (void *)io->virt, (unsigned long)io->size,
					vaddr_lo, vaddr_hi);
			}
			if (vaddr_sg_lo >= vaddr_lo &&
			    vaddr_sg_hi <= vaddr_hi) {
				if (fp) {
					fprintf(fp, "  SG[%d] matched REGION[%d]\n", i, j);
				}
				break;
			}
		}
		if (j == (int)ldev->device.num_regions) {
			/* Log the condition that triggers the original warning and return. */
			if (fp) {
				fprintf(fp, "WARNING: SG[%d] address %p..%p not in any region (num_regions=%u)\n",
					i, vaddr_sg_lo, vaddr_sg_hi, (unsigned int)ldev->device.num_regions);
			}
			metal_log(METAL_LOG_WARNING,
			  "%s,%s: input address isn't MMIO addr: 0x%x,%d.\n",
			__func__, ldev->dev_name, vaddr_sg_lo, sg_in[i].len);
			if (fp) {
				fclose(fp);
			}
			return -EINVAL;
		}
	}
	if (sg_out != sg_in) {
		if (fp) {
			fprintf(fp, "memcpy: copying %d entries from %p to %p (size=%zu)\n",
				 nents_in, (void *)sg_in, (void *)sg_out,
				 (size_t)(nents_in * (sizeof(struct metal_sg))));
		}
		memcpy(sg_out, sg_in, nents_in*(sizeof(struct metal_sg)));
	} else {
		if (fp) {
			fprintf(fp, "no memcpy: sg_out == sg_in (%p)\n", (void *)sg_in);
		}
	}

	if (fp) {
		fprintf(fp, "EXIT: %s returning %d\n", __func__, nents_in);
		fclose(fp);
	}
	return nents_in;
}
