#include <stdio.h>
#include <stdarg.h>

/* Probe helper that appends detailed logs to /tmp/logs/393.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/393.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

static int metal_uio_dev_dma_map(struct linux_bus *lbus,
                 struct linux_device *ldev,
                 uint32_t dir,
                 struct metal_sg *sg_in,
                 int nents_in,
                 struct metal_sg *sg_out)
{
    int i, j;
    void *vaddr_sg_lo, *vaddr_sg_hi, *vaddr_lo, *vaddr_hi;
    struct metal_io_region *io;

    (void)lbus;
    (void)dir;

    /* Entry probe */
    probe_log("metal_uio_dev_dma_map ENTER: ldev=%p name=%s sg_in=%p nents_in=%d sg_out=%p dir=%u\n",
              (void *)ldev,
              (ldev && ldev->dev_name) ? ldev->dev_name : "(null)",
              (void *)sg_in,
              nents_in,
              (void *)sg_out,
              (unsigned)dir);

    /* Check if the the input virt address is MMIO address */
    for (i = 0; i < nents_in; i++) {
        vaddr_sg_lo = sg_in[i].virt;
        vaddr_sg_hi = vaddr_sg_lo + sg_in[i].len;

        probe_log("  sg[%d]: virt=%p len=%lu -> hi=%p\n",
                  i, vaddr_sg_lo, (unsigned long)sg_in[i].len, vaddr_sg_hi);

        for (j = 0, io = ldev->device.regions;
             j < (int)ldev->device.num_regions; j++, io++) {
            vaddr_lo = io->virt;
            vaddr_hi = vaddr_lo + io->size;

            probe_log("    region[%d]: virt=%p size=%lu -> hi=%p ; comparing: sg_lo=%p >= reg_lo=%p ? %d ; sg_hi=%p <= reg_hi=%p ? %d\n",
                      j,
                      vaddr_lo, (unsigned long)io->size, vaddr_hi,
                      vaddr_sg_lo, vaddr_lo, (vaddr_sg_lo >= vaddr_lo),
                      vaddr_sg_hi, vaddr_hi, (vaddr_sg_hi <= vaddr_hi));

            if (vaddr_sg_lo >= vaddr_lo &&
                vaddr_sg_hi <= vaddr_hi) {
                probe_log("    -> sg[%d] matches region[%d]\n", i, j);
                break;
            }
        }
        if (j == (int)ldev->device.num_regions) {
            probe_log("  WARNING: input address isn't MMIO addr for sg[%d]: virt=%p len=%lu ; num_regions=%lu\n",
                      i, vaddr_sg_lo, (unsigned long)sg_in[i].len,
                      (unsigned long)ldev->device.num_regions);

            /* Mirror existing log for consistency */
            metal_log(METAL_LOG_WARNING,
              "%s,%s: input address isn't MMIO addr: 0x%x,%d.\n",
              __func__, ldev->dev_name, vaddr_sg_lo, sg_in[i].len);

            probe_log("metal_uio_dev_dma_map RETURN: -EINVAL (sg[%d] not in MMIO regions)\n", i);
            return -EINVAL;
        }
    }

    if (sg_out != sg_in) {
        probe_log("Copying %d sg entries from %p to %p (bytes=%lu)\n",
                  nents_in, (void *)sg_in, (void *)sg_out,
                  (unsigned long)(nents_in * (sizeof(struct metal_sg))));
        memcpy(sg_out, sg_in, nents_in*(sizeof(struct metal_sg)));
    } else {
        probe_log("No copy required: sg_out == sg_in (%p)\n", (void *)sg_in);
    }

    probe_log("metal_uio_dev_dma_map RETURN: %d\n", nents_in);
    return nents_in;
}
