#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <errno.h>

/* Probe helper: append formatted log messages to /tmp/logs/402.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f;
    va_list ap;

    /* Ensure directory exists; ignore errors if it already does */
    (void)mkdir("/tmp/logs", 0755);

    f = fopen("/tmp/logs/402.txt", "a");
    if (f == NULL) {
        /* If logging fails silently, do not change original behaviour */
        return;
    }

    va_start(ap, fmt);
    (void)vfprintf(f, fmt, ap);
    va_end(ap);
    (void)fclose(f);
}

/* Original function with logging probes inserted. */
static void metal_uio_dev_dma_unmap(struct linux_bus *lbus,
                                    struct linux_device *ldev,
                                    uint32_t dir,
                                    struct metal_sg *sg,
                                    int nents)
{
    /* Log entry and the raw state of all parameters so other agents can
       reproduce and gather evidence about any no-op casts or unused-parameter
       suppression. */
    probe_log("enter metal_uio_dev_dma_unmap\n");
    probe_log("  params before no-op casts: lbus=%p, ldev=%p, dir=%u, sg=%p, nents=%d\n",
              (void *)lbus, (unsigned)dir, (unsigned)dir, (void *)sg, nents);

    /* Keep the original explicit no-op casts that were reported as dead code.
       They remain to preserve the original source structure and to allow
       reproduction of the reported violation scenario. */
    (void) lbus;
    (void) ldev;
    (void) dir;
    (void) sg;
    (void) nents;

    /* Log after the no-op casts to demonstrate they do not alter state. */
    probe_log("  params after no-op casts: lbus=%p, ldev=%p, dir=%u, sg=%p, nents=%d\n",
              (void *)lbus, (unsigned)dir, (unsigned)dir, (void *)sg, nents);
    probe_log("exit metal_uio_dev_dma_unmap\n");
}
