static void metal_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/424.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int metal_uio_dev_open(struct linux_bus *lbus, struct linux_device *ldev)
{
    char *instance, path[SYSFS_PATH_MAX];
    struct linux_driver *ldrv = ldev->ldrv;
    unsigned long *phys, offset = 0, size = 0;
    struct metal_io_region *io;
    struct dlist *dlist;
    int result, i;
    void *virt;
    int irq_info;


    ldev->fd = -1;
    ldev->device.irq_info = (void *)-1;

    ldev->sdev = sysfs_open_device(lbus->bus_name, ldev->dev_name);
    if (!ldev->sdev) {
        metal_log(METAL_LOG_ERROR, "device %s:%s not found\n",
                  lbus->bus_name, ldev->dev_name);
        return -ENODEV;
    }
    metal_log(METAL_LOG_DEBUG, "opened sysfs device %s:%s\n",
              lbus->bus_name, ldev->dev_name);

    result = metal_uio_dev_bind(ldev, ldrv);
    if (result)
        return result;

    result = snprintf(path, sizeof(path), "%s/uio", ldev->sdev->path);
    if (result >= (int)sizeof(path))
        return -EOVERFLOW;
    dlist = sysfs_open_directory_list(path);
    if (!dlist) {
        metal_log(METAL_LOG_ERROR, "failed to scan class path %s\n",
                  path);
        return -errno;
    }

    dlist_for_each_data(dlist, instance, char) {
        result = snprintf(ldev->cls_path, sizeof(ldev->cls_path),
                          "%s/%s", path, instance);
        if (result >= (int)sizeof(ldev->cls_path))
            return -EOVERFLOW;
        result = snprintf(ldev->dev_path, sizeof(ldev->dev_path),
                          "/dev/%s", instance);
        if (result >= (int)sizeof(ldev->dev_path))
            return -EOVERFLOW;
        break;
    }
    sysfs_close_list(dlist);

    if (sysfs_path_is_dir(ldev->cls_path) != 0) {
        metal_log(METAL_LOG_ERROR, "invalid device class path %s\n",
                  ldev->cls_path);
        return -ENODEV;
    }

    i = 0;
    do {
        if (!access(ldev->dev_path, F_OK))
            break;
        usleep(10);
        i++;
    } while (i < 1000);
    if (i >= 1000) {
        metal_log(METAL_LOG_ERROR, "failed to open file %s, timeout.\n",
                  ldev->dev_path);
        return -ENODEV;
    }
    result = metal_open(ldev->dev_path, 0);
    if (result < 0) {
        metal_log(METAL_LOG_ERROR, "failed to open device %s\n",
                  ldev->dev_path, strerror(-result));
        return result;
    }
    ldev->fd = result;

    metal_log(METAL_LOG_DEBUG, "opened %s:%s as %s\n",
              lbus->bus_name, ldev->dev_name, ldev->dev_path);

    /* Log entering the region-mapping loop. The for header intentionally
     * initializes both i and result (this is the condition under analysis).
     */
    metal_probe_log("LOOP_START: for header about to run (the header will set i and result)");

    for (i = 0, result = 0; !result && i < METAL_MAX_DEVICE_REGIONS; i++) {
        /* Probe: log values as soon as we enter the loop body so the
         * initialization performed in the for header is captured. */
        phys = &ldev->region_phys[ldev->device.num_regions];
        metal_probe_log("LOOP_ENTER: iteration start: i=%d result=%d num_regions=%d phys_addr_ptr=%lu offset=%lu size=%lu",
                        i, result, ldev->device.num_regions, (unsigned long)phys, offset, size);

        /* Preserve original semantics: each of these calls is performed only
         * when result == 0. Expand the ternary chained assignments into
         * explicit checks so we can log before/after each call without
         * changing behavior. */
        if (!result) {
            metal_probe_log("CALL: metal_uio_read_map_attr(offset) i=%d", i);
            result = metal_uio_read_map_attr(ldev, i, "offset", &offset);
            metal_probe_log("RET: metal_uio_read_map_attr(offset) i=%d result=%d offset=%lu",
                            i, result, offset);
        }
        if (!result) {
            metal_probe_log("CALL: metal_uio_read_map_attr(addr) i=%d", i);
            result = metal_uio_read_map_attr(ldev, i, "addr", phys);
            metal_probe_log("RET: metal_uio_read_map_attr(addr) i=%d result=%d phys_value=%lu phys_ptr=%lu",
                            i, result, (unsigned long)(*phys), (unsigned long)phys);
        }
        if (!result) {
            metal_probe_log("CALL: metal_uio_read_map_attr(size) i=%d", i);
            result = metal_uio_read_map_attr(ldev, i, "size", &size);
            metal_probe_log("RET: metal_uio_read_map_attr(size) i=%d result=%d size=%lu",
                            i, result, size);
        }
        if (!result) {
            metal_probe_log("CALL: metal_map(fd=%d, i=%d, size=%lu) ", ldev->fd, i, size);
            result = metal_map(ldev->fd, i * getpagesize(), size, 0, 0, &virt);
            metal_probe_log("RET: metal_map i=%d result=%d virt_ptr=%lu",
                            i, result, (unsigned long)virt);
        }

        if (!result) {
            io = &ldev->device.regions[ldev->device.num_regions];
            metal_probe_log("MAP_SUCCESS: initializing io region num=%d i=%d phys=%lu size=%lu virt=%lu",
                            ldev->device.num_regions, i, (unsigned long)(*phys), size, (unsigned long)virt);
            metal_io_init(io, virt, phys, size, -1, 0, NULL);
            ldev->device.num_regions++;
            metal_probe_log("MAP_SUCCESS_DONE: num_regions now=%d", ldev->device.num_regions);
        } else {
            metal_probe_log("MAP_FAIL: iteration i=%d aborted with result=%d", i, result);
        }
    }

    metal_probe_log("LOOP_END: final i=%d result=%d num_regions=%d", i, result, ldev->device.num_regions);

    irq_info = 1;
    if (write(ldev->fd, &irq_info, sizeof(irq_info)) <= 0) {
        metal_log(METAL_LOG_INFO,
                  "%s: No IRQ for device %s.\n",
                  __func__, ldev->dev_name);
        ldev->device.irq_num =  0;
        ldev->device.irq_info = (void *)-1;
    } else {
        ldev->device.irq_num =  1;
        ldev->device.irq_info = (void *)(intptr_t)ldev->fd;
        metal_linux_irq_register_dev(&ldev->device, ldev->fd);
    }

    return 0;
}
