static void probe_log(const char *fmt, ...)
{
    /* Lightweight probe logger that appends to /tmp/logs/423.txt */
    FILE *f = fopen("/tmp/logs/423.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fclose(f);
}

static int metal_uio_dev_open(struct linux_bus *lbus, struct linux_device *ldev)
{
	char *instance, path[SYSFS_PATH_MAX];
	struct linux_driver *ldrv = ldev->ldrv;
	unsigned long *phys, offset = 0, size = 0;
	struct metal_io_region *io;
	struct dlist *dlist;
	int result, i;
	void *virt;
	int irq_info;

	/* Probe: entry */
	probe_log("[probe] metal_uio_dev_open ENTRY: bus='%s' device='%s' ldev=%p ldrv=%p\n",
	          lbus ? lbus->bus_name : "(null)", ldev ? ldev->dev_name : "(null)", (void *)ldev, (void *)ldrv);

	ldev->fd = -1;
	ldev->device.irq_info = (void *)-1;

	ldev->sdev = sysfs_open_device(lbus->bus_name, ldev->dev_name);
	if (!ldev->sdev) {
		metal_log(METAL_LOG_ERROR, "device %s:%s not found\n",
			  lbus->bus_name, ldev->dev_name);
		probe_log("[probe] sysfs_open_device FAILED for %s:%s\n",
		          lbus->bus_name, ldev->dev_name);
		return -ENODEV;
	}
	probe_log("[probe] opened sysfs device: sdev=%p path='%s'\n",
	          (void *)ldev->sdev, ldev->sdev ? ldev->sdev->path : "(null)");
	metal_log(METAL_LOG_DEBUG, "opened sysfs device %s:%s\n",
		  lbus->bus_name, ldev->dev_name);

	result = metal_uio_dev_bind(ldev, ldrv);
	if (result)
		return result;

	result = snprintf(path, sizeof(path), "%s/uio", ldev->sdev->path);
	if (result >= (int)sizeof(path))
		return -EOVERFLOW;
	dlist = sysfs_open_directory_list(path);
	if (!dlist) {
		metal_log(METAL_LOG_ERROR, "failed to scan class path %s\n",
			  path);
		probe_log("[probe] sysfs_open_directory_list FAILED for path='%s'\n", path);
		return -errno;
	}

	probe_log("[probe] scanning directory list at '%s' dlist=%p\n", path, (void *)dlist);

	dlist_for_each_data(dlist, instance, char) {
		/* Probe inside iterator: capture the data pointer and string contents. */
		if (instance)
			probe_log("[probe] iterator: instance ptr=%p instance_str='%s'\n", (void *)instance, instance);
		else
			probe_log("[probe] iterator: instance ptr=NULL\n");

		result = snprintf(ldev->cls_path, sizeof(ldev->cls_path),
				  "%s/%s", path, instance);
		if (result >= (int)sizeof(ldev->cls_path))
			return -EOVERFLOW;
		result = snprintf(ldev->dev_path, sizeof(ldev->dev_path),
				  "/dev/%s", instance);
		if (result >= (int)sizeof(ldev->dev_path))
			return -EOVERFLOW;
		probe_log("[probe] selected instance -> cls_path='%s' dev_path='%s'\n",
		          ldev->cls_path, ldev->dev_path);
		break;
	}
	sysfs_close_list(dlist);

	/* Probe: after directory scan */
	probe_log("[probe] after scan: cls_path='%s' dev_path='%s'\n",
	          ldev->cls_path, ldev->dev_path);

	if (sysfs_path_is_dir(ldev->cls_path) != 0) {
		metal_log(METAL_LOG_ERROR, "invalid device class path %s\n",
			  ldev->cls_path);
		probe_log("[probe] invalid device class path '%s'\n", ldev->cls_path);
		return -ENODEV;
	}

	i = 0;
	do {
		if (!access(ldev->dev_path, F_OK))
			break;
		usleep(10);
		i++;
	} while (i < 1000);
	if (i >= 1000) {
		metal_log(METAL_LOG_ERROR, "failed to open file %s, timeout.\n",
			  ldev->dev_path);
		probe_log("[probe] timeout waiting for dev_path='%s' after %d checks\n",
		          ldev->dev_path, i);
		return -ENODEV;
	}
	probe_log("[probe] device file appeared after %d iterations: '%s'\n", i, ldev->dev_path);

	result = metal_open(ldev->dev_path, 0);
	if (result < 0) {
		metal_log(METAL_LOG_ERROR, "failed to open device %s\n",
			  ldev->dev_path, strerror(-result));
		probe_log("[probe] metal_open FAILED for '%s' err=%d\n", ldev->dev_path, result);
		return result;
	}
	ldev->fd = result;

	metal_log(METAL_LOG_DEBUG, "opened %s:%s as %s\n",
		  lbus->bus_name, ldev->dev_name, ldev->dev_path);
	probe_log("[probe] device opened: fd=%d dev_path='%s'\n", ldev->fd, ldev->dev_path);

	for (i = 0, result = 0; !result && i < METAL_MAX_DEVICE_REGIONS; i++) {
		phys = &ldev->region_phys[ldev->device.num_regions];
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "offset", &offset));
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "addr", phys));
		result = (result ? result :
			 metal_uio_read_map_attr(ldev, i, "size", &size));
		result = (result ? result :
			 metal_map(ldev->fd, i * getpagesize(), size, 0, 0, &virt));
		if (!result) {
			io = &ldev->device.regions[ldev->device.num_regions];
			metal_io_init(io, virt, phys, size, -1, 0, NULL);
			ldev->device.num_regions++;
			probe_log("[probe] mapped region %d phys=%lu size=%lu virt=%p num_regions=%d\n",
			          i, (unsigned long)*phys, (unsigned long)size, virt, ldev->device.num_regions);
		}
	}

	irq_info = 1;
	if (write(ldev->fd, &irq_info, sizeof(irq_info)) <= 0) {
		metal_log(METAL_LOG_INFO,
			  "%s: No IRQ for device %s.\n",
			  __func__, ldev->dev_name);
		ldev->device.irq_num =  0;
		ldev->device.irq_info = (void *)-1;
		probe_log("[probe] no IRQ registered for device '%s' fd=%d\n", ldev->dev_name, ldev->fd);
	} else {
		ldev->device.irq_num =  1;
		ldev->device.irq_info = (void *)(intptr_t)ldev->fd;
		metal_linux_irq_register_dev(&ldev->device, ldev->fd);
		probe_log("[probe] IRQ registered: irq_num=%d irq_info=%p fd=%d\n",
		          ldev->device.irq_num, ldev->device.irq_info, ldev->fd);
	}

	probe_log("[probe] metal_uio_dev_open EXIT success ldev=%p fd=%d num_regions=%d\n",
	          (void *)ldev, ldev->fd, ldev->device.num_regions);

	return 0;
}
