static void probe_log_basic(const char *s)
{
    FILE *f = fopen("/tmp/logs/423.txt", "a");
    if (f) {
        fprintf(f, "%s\n", s);
        fclose(f);
    }
}

static void probe_log_str(const char *label, const char *s)
{
    FILE *f = fopen("/tmp/logs/423.txt", "a");
    if (f) {
        fprintf(f, "%s %s\n", label, s ? s : "(null)");
        fclose(f);
    }
}

static void probe_log_ptr(const char *label, const void *p)
{
    FILE *f = fopen("/tmp/logs/423.txt", "a");
    if (f) {
        fprintf(f, "%s %p\n", label, p);
        fclose(f);
    }
}

static void probe_log_num(const char *label, long v)
{
    FILE *f = fopen("/tmp/logs/423.txt", "a");
    if (f) {
        fprintf(f, "%s %ld\n", label, v);
        fclose(f);
    }
}

static void probe_log_region(int idx, unsigned long offset, unsigned long phys, unsigned long size, int result)
{
    FILE *f = fopen("/tmp/logs/423.txt", "a");
    if (f) {
        fprintf(f, "region[%d]: offset=%lu phys=%lu size=%lu result=%d\n",
                idx, offset, phys, size, result);
        fclose(f);
    }
}

static int metal_uio_dev_open(struct linux_bus *lbus, struct linux_device *ldev)
{
    char *instance, path[SYSFS_PATH_MAX];
    struct linux_driver *ldrv = ldev->ldrv;
    unsigned long *phys, offset = 0, size = 0;
    struct metal_io_region *io;
    struct dlist *dlist;
    int result, i;
    void *virt;
    int irq_info;


    /* Probe: entry and pointers */
    probe_log_basic("metal_uio_dev_open: entry");
    probe_log_ptr("lbus:", (const void *)lbus);
    probe_log_ptr("ldev:", (const void *)ldev);
    probe_log_ptr("ldrv (from ldev->ldrv):", (const void *)ldrv);

    ldev->fd = -1;
    ldev->device.irq_info = (void *)-1;

    ldev->sdev = sysfs_open_device(lbus->bus_name, ldev->dev_name);
    if (!ldev->sdev) {
        metal_log(METAL_LOG_ERROR, "device %s:%s not found\n",
                  lbus->bus_name, ldev->dev_name);
        probe_log_str("sysfs_open_device failed for:", ldev->dev_name);
        return -ENODEV;
    }
    metal_log(METAL_LOG_DEBUG, "opened sysfs device %s:%s\n",
              lbus->bus_name, ldev->dev_name);
    probe_log_str("opened sysfs device:", ldev->sdev->path);

    probe_log_ptr("about to call metal_uio_dev_bind with ldrv:", (const void *)ldrv);
    result = metal_uio_dev_bind(ldev, ldrv);
    probe_log_num("metal_uio_dev_bind result:", (long)result);
    if (result)
        return result;

    result = snprintf(path, sizeof(path), "%s/uio", ldev->sdev->path);
    if (result >= (int)sizeof(path))
        return -EOVERFLOW;
    dlist = sysfs_open_directory_list(path);
    if (!dlist) {
        metal_log(METAL_LOG_ERROR, "failed to scan class path %s\n",
                  path);
        probe_log_str("sysfs_open_directory_list failed for:", path);
        return -errno;
    }

    dlist_for_each_data(dlist, instance, char) {
        result = snprintf(ldev->cls_path, sizeof(ldev->cls_path),
                          "%s/%s", path, instance);
        if (result >= (int)sizeof(ldev->cls_path))
            return -EOVERFLOW;
        result = snprintf(ldev->dev_path, sizeof(ldev->dev_path),
                          "/dev/%s", instance);
        if (result >= (int)sizeof(ldev->dev_path))
            return -EOVERFLOW;
        probe_log_str("found uio instance:", instance);
        probe_log_str("set cls_path:", ldev->cls_path);
        probe_log_str("set dev_path:", ldev->dev_path);
        break;
    }
    sysfs_close_list(dlist);

    if (sysfs_path_is_dir(ldev->cls_path) != 0) {
        metal_log(METAL_LOG_ERROR, "invalid device class path %s\n",
                  ldev->cls_path);
        probe_log_str("invalid cls_path:", ldev->cls_path);
        return -ENODEV;
    }

    i = 0;
    do {
        if (!access(ldev->dev_path, F_OK))
            break;
        usleep(10);
        i++;
    } while (i < 1000);
    if (i >= 1000) {
        metal_log(METAL_LOG_ERROR, "failed to open file %s, timeout.\n",
                  ldev->dev_path);
        probe_log_str("dev_path timeout:", ldev->dev_path);
        return -ENODEV;
    }
    result = metal_open(ldev->dev_path, 0);
    if (result < 0) {
        metal_log(METAL_LOG_ERROR, "failed to open device %s\n",
                  ldev->dev_path, strerror(-result));
        probe_log_str("metal_open failed for:", ldev->dev_path);
        return result;
    }
    ldev->fd = result;
    probe_log_num("opened device fd:", (long)ldev->fd);

    metal_log(METAL_LOG_DEBUG, "opened %s:%s as %s\n",
              lbus->bus_name, ldev->dev_name, ldev->dev_path);

    for (i = 0, result = 0; !result && i < METAL_MAX_DEVICE_REGIONS; i++) {
        phys = &ldev->region_phys[ldev->device.num_regions];
        result = (result ? result :
                 metal_uio_read_map_attr(ldev, i, "offset", &offset));
        result = (result ? result :
                 metal_uio_read_map_attr(ldev, i, "addr", phys));
        result = (result ? result :
                 metal_uio_read_map_attr(ldev, i, "size", &size));
        result = (result ? result :
                 metal_map(ldev->fd, i * getpagesize(), size, 0, 0, &virt));
        probe_log_region(i, offset, phys ? *phys : 0UL, size, result);
        if (!result) {
            io = &ldev->device.regions[ldev->device.num_regions];
            metal_io_init(io, virt, phys, size, -1, 0, NULL);
            ldev->device.num_regions++;
            probe_log_num("incremented num_regions to:", (long)ldev->device.num_regions);
        }
    }

    irq_info = 1;
    if (write(ldev->fd, &irq_info, sizeof(irq_info)) <= 0) {
        metal_log(METAL_LOG_INFO,
                  "%s: No IRQ for device %s.\n",
                  __func__, ldev->dev_name);
        ldev->device.irq_num =  0;
        ldev->device.irq_info = (void *)-1;
        probe_log_basic("No IRQ for device; set irq_num=0 and irq_info=(void *)-1");
    } else {
        ldev->device.irq_num =  1;
        ldev->device.irq_info = (void *)(intptr_t)ldev->fd;
        probe_log_num("irq write succeeded, irq_num set to:", (long)ldev->device.irq_num);
        probe_log_ptr("irq_info pointer set to:", ldev->device.irq_info);
        metal_linux_irq_register_dev(&ldev->device, ldev->fd);
    }

    probe_log_basic("metal_uio_dev_open: exit success");
    return 0;
}
