#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <stdlib.h>
#include <string.h>

/* Probe helper: append logs to /tmp/logs/433.txt. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/433.txt", "a");
    if (!f)
        return;

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputs("\n", f);
    fclose(f);
}

static int metal_uio_read_map_attr(struct linux_device *ldev,
                   unsigned int index,
                   const char *name,
                   unsigned long *value)
{
    const char *cls = NULL;
    struct sysfs_attribute *attr;
    char path[SYSFS_PATH_MAX];
    int result;

    /* Log entry and parameter state */
    probe_log("ENTER: %s: ldev=%p, name=%s, index=%u, value_ptr=%p",
              __func__, (void *)ldev, name ? name : "(null)", index, (void *)value);

    if (ldev)
        cls = ldev->cls_path;
    probe_log("STATE: cls_path=%s", cls ? cls : "(null)");

    result = snprintf(path, sizeof(path), "%s/maps/map%u/%s", cls ? cls : "", index, name ? name : "");
    probe_log("SNPRINTF: result=%d, path=%s", result, path);
    if (result >= (int)sizeof(path)) {
        probe_log("ERROR: path overflow (result=%d, max=%zu)", result, sizeof(path));
        return -EOVERFLOW;
    }
    attr = sysfs_open_attribute(path);
    probe_log("sysfs_open_attribute: returned attr=%p", (void *)attr);
    if (!attr || sysfs_read_attribute(attr) != 0) {
        int err = errno; /* capture errno for logging */
        probe_log("sysfs_read_attribute failed: attr=%p, errno=%d (%s)", (void *)attr, err, err ? strerror(err) : "(none)");
        sysfs_close_attribute(attr);
        return -err;
    }

    probe_log("attr->value (raw)=%s", attr->value ? attr->value : "(null)");

    /* Preserve original behavior: parse unsigned long from attribute value */
    *value = strtoul(attr->value, NULL, 0);
    probe_log("PARSED: *value=%lu", *value);

    sysfs_close_attribute(attr);
    probe_log("EXIT: success, returning 0");
    return 0;
}
