#include <stdio.h>
#include <stdint.h>
#include <errno.h>
#include <string.h>

/* Probe helper: append observations to /tmp/logs/540.txt. */
static void metal_probe_log(const char *stage, void *addr, off_t offset,
                            uint64_t entry, int err, unsigned long phys, int have_phys)
{
    FILE *f = fopen("/tmp/logs/540.txt", "a");
    if (!f)
        return;

    /* stage: textual description; addr: caller-supplied pointer; */
    fprintf(f, "[metal_virt2phys] %s: addr=%p", stage, addr);

    /* offset and entry may be unknown for some stages; print them anyway. */
    fprintf(f, " offset=0x%llx entry=0x%llx", (unsigned long long)offset, (unsigned long long)entry);

    /* Record errno-related information if provided. */
    fprintf(f, " err=%d", err);
    if (err != 0) {
        /* Attempt to print a human-readable error string without changing errno. */
        char buf[128];
        const char *s = strerror(err);
        if (s)
            snprintf(buf, sizeof(buf), "%s", s);
        else
            snprintf(buf, sizeof(buf), "(unknown error %d)", err);
        fprintf(f, " (%s)", buf);
    }

    if (have_phys)
        fprintf(f, " phys=0x%lx", phys);

    fprintf(f, "\n");
    fclose(f);
}

static int metal_virt2phys(void *addr, unsigned long *phys)
{
	off_t offset;
	uint64_t entry;
	int error;

	/* Log function entry and the pagemap fd value indirectly (offset==0 used as placeholder). */
	metal_probe_log("enter", addr, 0, 0, 0, 0, 0);

	if (_metal.pagemap_fd < 0) {
		/* Log reason for early return. */
		metal_probe_log("pagemap_fd_invalid", addr, 0, 0, errno, 0, 0);
		return -EINVAL;
	}

	offset = ((uintptr_t)addr >> _metal.page_shift) * sizeof(entry);
	/* Log computed offset before pread. */
	metal_probe_log("computed_offset", addr, offset, 0, 0, 0, 0);

	error = pread(_metal.pagemap_fd, &entry, sizeof(entry), offset);
	if (error < 0) {
		/* Capture errno at failure point and log details to file for reproducibility. */
		int saved_errno = errno;
		metal_log(METAL_LOG_ERROR, "failed pagemap pread (offset %llx) - %s\n",
			  (unsigned long long)offset, strerror(errno));
		metal_probe_log("pread_failed", addr, offset, 0, saved_errno, 0, 0);
		return -errno;
	}

	/* Check page present and not swapped. */
	if ((entry >> 62) != 2) {
		metal_log(METAL_LOG_ERROR, "pagemap page not present, %llx -> %llx\n",
			  (unsigned long long)offset, (unsigned long long)entry);
		/* Log the pagemap entry value that caused the failure. */
		metal_probe_log("page_not_present", addr, offset, entry, 0, 0, 0);
		return -ENOENT;
	}

	*phys = (entry & ((1ULL << 54) - 1)) << _metal.page_shift;
	/* Log successful translation and the resulting physical address. */
	metal_probe_log("success", addr, offset, entry, 0, *phys, 1);
	return 0;
}
