#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>
#include <errno.h>
#include <string.h>
#include <unistd.h>
#include <sys/types.h>

/* Probe helper: append diagnostic logs to /tmp/logs/543.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/543.txt", "a");
    if (f == NULL) {
        /* If logging can't be opened, silently return to preserve original behavior */
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

static int metal_virt2phys(void *addr, unsigned long *phys)
{
	off_t offset;
	uint64_t entry;
	int error;

	/* Log function entry and key inputs. Record the pointer value and its integer cast. */
	probe_log("enter metal_virt2phys: addr=%p addr_uint=%" PRIuPTR " phys_ptr=%p _metal.pagemap_fd=%d _metal.page_shift=%u",
	          addr, (uintptr_t)addr, (void *)phys, _metal.pagemap_fd, (unsigned)_metal.page_shift);

	if (_metal.pagemap_fd < 0) {
		probe_log("pagemap_fd check failed: _metal.pagemap_fd=%d -> return -EINVAL", _metal.pagemap_fd);
		return -EINVAL;
	}

	offset = ((uintptr_t)addr >> _metal.page_shift) * sizeof(entry);
	probe_log("computed offset: addr_uint=%" PRIuPTR " >> page_shift=%u -> offset=%llu",
	          (uintptr_t)addr, (unsigned)_metal.page_shift, (unsigned long long)offset);

	error = pread(_metal.pagemap_fd, &entry, sizeof(entry), offset);
	probe_log("pread returned: error=%d entry=0x%016llx", error, (unsigned long long)entry);
	if (error < 0) {
		probe_log("failed pagemap pread (offset %llx) - %s", (unsigned long long)offset, strerror(errno));
		return -errno;
	}

	/* Check page present and not swapped. */
	if ((entry >> 62) != 2) {
		probe_log("pagemap page not present: offset=0x%llx entry=0x%llx -> return -ENOENT",
		          (unsigned long long)offset, (unsigned long long)entry);
		return -ENOENT;
	}

	/* Compute physical address and log result before returning. */
	*phys = (entry & ((1ULL << 54) - 1)) << _metal.page_shift;
	probe_log("computed phys: entry=0x%016llx page_shift=%u -> phys=0x%lx",
	          (unsigned long long)entry, (unsigned)_metal.page_shift, *phys);
	return 0;
}
