#include <stdio.h>
#include <fcntl.h>
#include <string.h>
#include <errno.h>

// Assuming necessary typedefs, macros, and the 'OS_DEBUG' macro are defined elsewhere as in the original context.

// Modified OS_FileOpen_Impl with probe logs
int32 OS_FileOpen_Impl(const OS_object_token_t *token, const char *local_path, int32 flags, int32 access_mode)
{
    int os_perm;
    int os_mode;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    switch (access_mode)
    {
        case OS_WRITE_ONLY:
            os_perm = O_WRONLY;
            break;
        case OS_READ_ONLY:
            os_perm = O_RDONLY;
            break;
        case OS_READ_WRITE:
            os_perm = O_RDWR;
            break;
        default:
            // Log unreachable default case
            FILE *logfile = fopen("/tmp/logs/49.txt", "a");
            if (logfile != NULL)
            {
                fprintf(logfile, "Unreachable code reached: Invalid access_mode %d\n", access_mode);
                fclose(logfile);
            }
            return OS_ERROR;
        // Note: No break here to simulate unreachable code, but in reality, 'return' prevents reaching here.
    }

    if (flags & OS_FILE_FLAG_CREATE)
    {
        os_perm |= O_CREAT;
    }
    if (flags & OS_FILE_FLAG_TRUNCATE)
    {
        os_perm |= O_TRUNC;
    }

    os_perm |= OS_IMPL_REGULAR_FILE_FLAGS;

    os_mode = S_IRUSR | S_IWUSR | S_IRGRP | S_IWGRP | S_IROTH | S_IWOTH;

    impl->fd = open(local_path, os_perm, os_mode);

    if (impl->fd < 0)
    {
        FILE *logfile = fopen("/tmp/logs/49.txt", "a");
        if (logfile != NULL)
        {
            fprintf(logfile, "open(%s): %s\n", local_path, strerror(errno));
            fclose(logfile);
        }
        return OS_ERROR;
    }

    impl->selectable = ((os_perm & O_NONBLOCK) != 0);

    return OS_SUCCESS;
}
