void log_violation(const OS_object_token_t *token, const char *local_path, int32 flags, int32 access_mode) {
    FILE *logfile = fopen("/tmp/logs/51.txt", "a");
    if (logfile == NULL) return;
    fprintf(logfile, "Entering OS_FileOpen_Impl with token at %p, path: %s, flags: 0x%x, access_mode: %d\n", (void*)token, local_path, flags, access_mode);
    fclose(logfile);
}

int32 OS_FileOpen_Impl(const OS_object_token_t *token, const char *local_path, int32 flags, int32 access_mode)
{
    log_violation(token, local_path, flags, access_mode);
    int os_perm;
    int os_mode;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    switch (access_mode)
    {
        case OS_WRITE_ONLY:
            os_perm = O_WRONLY;
            break;
        case OS_READ_ONLY:
            os_perm = O_RDONLY;
            break;
        case OS_READ_WRITE:
            os_perm = O_RDWR;
            break;
        default:
            return OS_ERROR;
    }

    if (flags & OS_FILE_FLAG_CREATE)
    {
        os_perm |= O_CREAT;
    }
    if (flags & OS_FILE_FLAG_TRUNCATE)
    {
        os_perm |= O_TRUNC;
    }

    os_perm |= OS_IMPL_REGULAR_FILE_FLAGS;

    os_mode = S_IRUSR | S_IWUSR | S_IRGRP | S_IWGRP | S_IROTH | S_IWOTH;

    impl->fd = open(local_path, os_perm, os_mode);

    if (impl->fd < 0)
    {
        OS_DEBUG("open(%s): %s\n", local_path, strerror(errno));
        return OS_ERROR;
    }

    impl->selectable = ((os_perm & O_NONBLOCK) != 0);

    return OS_SUCCESS;
}
