// Test code to demonstrate rule violation due to preprocessor directive 
#include <stdio.h>
#include <string.h>

int32 OS_SocketAddrToString_Impl(char *buffer, size_t buflen, const OS_SockAddr_t *Addr)
{
    const void *                  addrbuffer;
    const OS_SockAddr_Accessor_t *Accessor;

    Accessor = (const OS_SockAddr_Accessor_t *)&Addr->AddrData;

    switch (Accessor->sa.sa_family)
    {
        case AF_INET:
            addrbuffer = &Accessor->sa_in.sin_addr;
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrbuffer = &Accessor->sa_in6.sin6_addr;
            break;
#endif
        default:
            return OS_ERR_BAD_ADDRESS;
            break;
    }

    if (inet_ntop(Accessor->sa.sa_family, addrbuffer, buffer, buflen) == NULL)
    {
        return OS_ERROR;
    }

    return OS_SUCCESS;
}

// Logging probe functions
#include <fcntl.h>
#include <unistd.h>

void log_violation(const char* message) {
    int fd = open(" /tmp/logs/104.txt", O_WRONLY | O_APPEND | O_CREAT, 0644);
    if (fd >= 0) {
        dprintf(fd, "Violation: %s\n", message);
        close(fd);
    }
}

// Function to perform a test run, demonstrating the conditional compilation usage
void test_rule_violation() {
    log_violation("Usage of preprocessor directive #ifdef OS_NETWORK_SUPPORTS_IPV6 detected, potential language extension.");
}
