int32 OS_SocketRecvFrom_Impl(const OS_object_token_t *token, void *buffer, size_t buflen, OS_SockAddr_t *RemoteAddr, int32 timeout);

void log_violation(const OS_SockAddr_t *RemoteAddr)
{
    FILE *file = fopen("/tmp/logs/37.txt", "a");
    if (file != NULL)
    {
        fprintf(file, "Violation path: RemoteAddr is modified. ActualLength: %u\n", RemoteAddr->ActualLength);
        fclose(file);
    }
}

int32 OS_SocketRecvFrom_Impl(const OS_object_token_t *token, void *buffer, size_t buflen, OS_SockAddr_t *RemoteAddr, int32 timeout)
{
    int32 return_code;
    int os_result;
    int waitflags;
    uint32 operation;
    struct sockaddr *sa;
    socklen_t addrlen;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (RemoteAddr == NULL)
    {
        sa = NULL;
        addrlen = 0;
    }
    else
    {
        addrlen = OS_SOCKADDR_MAX_LEN;
        sa = (struct sockaddr *)&RemoteAddr->AddrData;
    }

    operation = OS_STREAM_STATE_READABLE;

    if (impl->selectable)
    {
        waitflags = MSG_DONTWAIT;
        return_code = OS_SelectSingle_Impl(token, &operation, timeout);
    }
    else
    {
        if (timeout == 0)
        {
            waitflags = MSG_DONTWAIT;
        }
        else
        {
            waitflags = 0;
        }
        return_code = OS_SUCCESS;
    }

    if (return_code == OS_SUCCESS)
    {
        if ((operation & OS_STREAM_STATE_READABLE) == 0)
        {
            return_code = OS_ERROR_TIMEOUT;
        }
        else
        {
            os_result = recvfrom(impl->fd, buffer, buflen, waitflags, sa, &addrlen);
            if (os_result < 0)
            {
                if (errno == EAGAIN || errno == EWOULDBLOCK)
                {
                    return_code = OS_QUEUE_EMPTY;
                }
                else
                {
                    OS_DEBUG("recvfrom: %s\n", strerror(errno));
                    return_code = OS_ERROR;
                }
            }
            else
            {
                return_code = os_result;

                if (RemoteAddr != NULL)
                {
                    RemoteAddr->ActualLength = addrlen;
                }
            }
        }
    }

    // Log the violation scenario if RemoteAddr was modified
    if (RemoteAddr != NULL)
    {
        log_violation(RemoteAddr);
    }

    return return_code;
}
