#include <stdio.h>
#include <stddef.h>
#include <string.h>

static void
log_probe (const char *stage, const void *res_ptr, const char *note)
{
  FILE *f = fopen ("/tmp/logs/494.txt", "a");
  if (NULL == f)
    return;
  fprintf (f, "stage: %s\n", stage);
  fprintf (f, "res_ptr: %p\n", res_ptr);
  if (note != NULL)
    fprintf (f, "note: %s\n", note);
  fprintf (f, "----\n");
  fclose (f);
}

static enum MHD_Result
ahc_cb (void *cls, struct MHD_Connection *con, const char *url,
        const char *method, const char *version, const char *upload_data,
        size_t *upload_data_size, void **req_cls)
{
  struct MHD_Response *res;
  const char *upg_header;
  const char *con_header;
  const char *ws_version_header;
  const char *ws_key_header;
  char *ws_ac_value;
  enum MHD_Result ret;
  size_t key_size;

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  log_probe ("entry", NULL, "ahc_cb entered");

  if (NULL == *req_cls)
  {
    *req_cls = (void *) 1;
    log_probe ("first_req", NULL, "initial request class set");
    return MHD_YES;
  }
  *req_cls = NULL;
  upg_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_UPGRADE);
  con_header = MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                            MHD_HTTP_HEADER_CONNECTION);
  if (MHD_NO == is_websocket_request (con, upg_header, con_header))
  {
    log_probe ("not_ws", NULL, "not a websocket request, sending chat page");
    return send_chat_page (con);
  }
  if ((0 != strcmp (method, MHD_HTTP_METHOD_GET))
      || (0 != strcmp (version, MHD_HTTP_VERSION_1_1)))
  {
    log_probe ("bad_method_or_version", NULL, "bad method or version");
    return send_bad_request (con);
  }
  ws_version_header =
    MHD_lookup_connection_value (con, MHD_HEADER_KIND,
                                 MHD_HTTP_HEADER_SEC_WEBSOCKET_VERSION);
  if ((NULL == ws_version_header)
      || (0 != strcmp (ws_version_header, WS_SEC_WEBSOCKET_VERSION)))
  {
    log_probe ("bad_ws_version", NULL, "missing or unsupported websocket version");
    return send_upgrade_required (con);
  }
  ret = MHD_lookup_connection_value_n (con, MHD_HEADER_KIND,
                                       MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY,
                                       strlen (
                                         MHD_HTTP_HEADER_SEC_WEBSOCKET_KEY),
                                       &ws_key_header, &key_size);
  if ((MHD_NO == ret) || (key_size != WS_KEY_LEN))
  {
    log_probe ("bad_key", NULL, "missing or wrong size websocket key");
    return send_bad_request (con);
  }
  ret = ws_get_accept_value (ws_key_header, &ws_ac_value);
  if (MHD_NO == ret)
  {
    log_probe ("ws_get_accept_failed", NULL, "ws_get_accept_value failed");
    return ret;
  }

  /* Create the upgrade response as in original logic */
  res = MHD_create_response_for_upgrade (&uh_cb, NULL);
  log_probe ("created_response", res, "response created for upgrade");

  /*
   * Probe scenario that exercises the code path where memcmp is used to
   * compare a null-terminated header value to the literal "close".
   * We add the "Connection: close" header first and then add another
   * connection header value to force the update path where the existing
   * header entry is inspected (this triggers the mhd_assert that contains
   * the memcmp in add_response_header_connection).
   *
   * These extra calls are probes only and intended to record the control
   * flow and state. They preserve the rest of ahc_cb logic as much as
   * possible.
   */
  (void) MHD_add_response_header (res, MHD_HTTP_HEADER_CONNECTION, "close");
  log_probe ("after_add_connection_close", res, "added Connection: close");

  /* Second addition triggers the update branch in add_response_header_connection */
  (void) MHD_add_response_header (res, MHD_HTTP_HEADER_CONNECTION, "keep-alive");
  log_probe ("after_add_connection_keepalive", res, "added Connection: keep-alive to trigger update path");

  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_SEC_WEBSOCKET_ACCEPT,
                               ws_ac_value))
  {
    free (ws_ac_value);
    MHD_destroy_response (res);
    log_probe ("add_accept_failed", res, "failed to add Sec-WebSocket-Accept header");
    return MHD_NO;
  }
  free (ws_ac_value);
  if (MHD_YES !=
      MHD_add_response_header (res, MHD_HTTP_HEADER_UPGRADE, WS_UPGRADE_VALUE))
  {
    MHD_destroy_response (res);
    log_probe ("add_upgrade_failed", res, "failed to add Upgrade header");
    return MHD_NO;
  }
  ret = MHD_queue_response (con, MHD_HTTP_SWITCHING_PROTOCOLS, res);
  log_probe ("queued_response", res, (ret == MHD_YES) ? "queued ok" : "queue failed");
  MHD_destroy_response (res);
  return ret;
}
