static void log_to_file(const char *msg)
{
  FILE *fp = fopen("/tmp/logs/614.txt", "a");
  if (fp != NULL)
  {
    fprintf(fp, "%s\n", msg);
    fclose(fp);
  }
}

static enum MHD_Result
answer_to_connection (void *cls, struct MHD_Connection *connection,
                      const char *url, const char *method,
                      const char *version, const char *upload_data,
                      size_t *upload_data_size, void **req_cls)
{
  struct MHD_BasicAuthInfo *auth_info;
  enum MHD_Result ret;
  struct MHD_Response *response;
  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */
  (void) upload_data;       /* Unused. Silent compiler warning. */
  (void) upload_data_size;  /* Unused. Silent compiler warning. */

  /* Log entry and method */
  log_to_file("answer_to_connection: entered");
  if (method != NULL)
  {
    char buf[128];
    /* safe formatting for log */
    (void) snprintf(buf, sizeof(buf), "method=%s", method);
    log_to_file(buf);
  }

  if (0 != strcmp (method, "GET"))
  {
    log_to_file("answer_to_connection: method != GET -> MHD_NO");
    return MHD_NO;
  }
  if (NULL == *req_cls)
  {
    log_to_file("answer_to_connection: first request, storing req_cls");
    *req_cls = connection;
    return MHD_YES;
  }
  auth_info = MHD_basic_auth_get_username_password3 (connection);
  if (NULL == auth_info)
  {
    log_to_file("answer_to_connection: auth_info == NULL (no auth provided)");
    static const char *page =
      "<html><body>Authorization required</body></html>";
    response = MHD_create_response_from_buffer_static (strlen (page), page);
    ret = MHD_queue_basic_auth_required_response3 (connection,
                                                   "admins",
                                                   MHD_YES,
                                                   response);
  }
  else if ((strlen ("root") != auth_info->username_len) ||
           (0 != memcmp (auth_info->username, "root",
                         auth_info->username_len)) ||
           /* The next check against NULL is optional,
            * if 'password' is NULL then 'password_len' is always zero. */
           (NULL == auth_info->password) ||
           (strlen ("pa$$w0rd") != auth_info->password_len) ||
           (0 != memcmp (auth_info->password, "pa$$w0rd",
                         auth_info->password_len)))
  {
    /* Log the exact state that leads to rejection for the violation scenario */
    {
      char buf[256];
      (void) snprintf(buf, sizeof(buf),
                      "answer_to_connection: auth provided: username_len=%zu, password_len=%zu",
                      auth_info->username_len, auth_info->password_len);
      log_to_file(buf);
      if (auth_info->username != NULL)
      {
        (void) snprintf(buf, sizeof(buf), "username='%s'", auth_info->username);
        log_to_file(buf);
      }
      if (auth_info->password != NULL)
      {
        (void) snprintf(buf, sizeof(buf), "password='%s'", auth_info->password);
        log_to_file(buf);
      }
      /* Log results of the comparisons used in the condition */
      {
        int cmp_user = memcmp(auth_info->username, "root",
                              auth_info->username_len);
        int cmp_pass = (auth_info->password != NULL) ?
                       memcmp(auth_info->password, "pa$$w0rd",
                              auth_info->password_len) : 1;
        (void) snprintf(buf, sizeof(buf), "strcmp_len_root=%zu vs username_len=%zu, memcmp_user=%d, memcmp_pass=%d",
                        strlen("root"), auth_info->username_len, cmp_user, cmp_pass);
        log_to_file(buf);
      }
    }

    static const char *page =
      "<html><body>Wrong username or password</body></html>";
    response = MHD_create_response_from_buffer_static (strlen (page), page);
    ret = MHD_queue_basic_auth_required_response3 (connection,
                                                   "admins",
                                                   MHD_YES,
                                                   response);
  }
  else
  {
    log_to_file("answer_to_connection: auth succeeded");
    static const char *page = "<html><body>A secret.</body></html>";
    response = MHD_create_response_from_buffer_static (strlen (page), page);
    ret = MHD_queue_response (connection, MHD_HTTP_OK, response);
  }
  if (NULL != auth_info)
    MHD_free (auth_info);
  MHD_destroy_response (response);
  return ret;
}
