#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <stdbool.h>
#include <stddef.h>

/* Use an enum to avoid defining macros */
enum { XBUF_SIZE = 1024 };

/* Minimal constants to allow compilation */
const int MHD_NO = 0;
const int MHD_POSTDATA_KIND = 0;
const int PP_Error = 1;

/* Minimal assert replacement (function, not macro) */
void mhd_assert (int cond)
{
  if (!cond)
    abort();
}

/* Minimal prototype for MHD_http_unescape used by process_value; keep behaviour simple */
size_t MHD_http_unescape (char *val)
{
  return strlen(val);
}

/* The violating function: the for loop's third clause calls strchr on memory that is
   modified in the loop body (the body replaces '+' with ' '). This demonstrates the
   Rule 14.2 violation: the third clause uses objects modified in the loop body. */
void
MHD_unescape_plus (char *arg)
{
  /* Append logs to the requested file for tracing the control path and state */
  FILE *f = fopen("/tmp/logs/652.txt", "a");
  if (f != NULL)
  {
    fprintf(f, "MHD_unescape_plus: entry arg=%p\n", (void *) arg);
  }

  char *p;
  /* The following for loop is the point of interest: the third clause calls
     strchr(p + 1, '+') which reads the same string object that the loop body
     modifies via '*p = ' ''. That use of an object modified in the loop body
     violates MISRA C 2023 Rule 14.2. */
  for (p = strchr (arg, '+'); NULL != p; p = strchr (p + 1, '+'))
  {
    if (f != NULL)
    {
      fprintf(f, "  iter: p=%p index=%ld char_before=%c\n",
              (void *) p,
              (long) (p - arg),
              *p);
    }
    *p = ' ';
    if (f != NULL)
    {
      fprintf(f, "  iter: char_after=%c\n", *p);
    }
  }

  if (f != NULL)
    fclose(f);
}

/* Minimal definition of ikvi callback type used by process_value */
typedef int (*ikvi_cb_t) (void *cls,
                          int kind,
                          const char *key,
                          void *a,
                          void *b,
                          void *c,
                          char *xbuf,
                          size_t offset,
                          size_t xoff);

/* Minimal post processor structure with only the fields used by process_value */
struct MHD_PostProcessor
{
  size_t xbuf_pos;
  char xbuf[XBUF_SIZE];
  bool must_ikvi;
  ikvi_cb_t ikvi;
  void *cls;
  size_t value_offset;
  int state;
};

/* process_value preserved as closely as possible; it calls MHD_unescape_plus above
   so the logging from that function will record the violating control path. */
static void
process_value (struct MHD_PostProcessor *pp,
               const char *value_start,
               const char *value_end,
               const char *last_escape)
{
  char xbuf[XBUF_SIZE + 1];
  size_t xoff;

  mhd_assert (pp->xbuf_pos < sizeof (xbuf));
  /* 'value_start' and 'value_end' must be either both non-NULL or both NULL */
  mhd_assert ( (NULL == value_start) || (NULL != value_end) );
  mhd_assert ( (NULL != value_start) || (NULL == value_end) );
  mhd_assert ( (NULL == last_escape) || (NULL != value_start) );
  /* move remaining input from previous round into processing buffer */
  if (0 != pp->xbuf_pos)
    memcpy (xbuf,
            pp->xbuf,
            pp->xbuf_pos);
  xoff = pp->xbuf_pos;
  pp->xbuf_pos = 0;
  if ( (NULL != last_escape) &&
       (((size_t) (value_end - last_escape)) < sizeof (pp->xbuf)) )
  {
    mhd_assert (value_end >= last_escape);
    pp->xbuf_pos = (size_t) (value_end - last_escape);
    memcpy (pp->xbuf,
            last_escape,
            (size_t) (value_end - last_escape));
    value_end = last_escape;
  }
  while ( (value_start != value_end) ||
          (pp->must_ikvi) ||
          (xoff > 0) )
  {
    size_t delta = (size_t) (value_end - value_start);
    bool cut = false;
    size_t clen = 0;

    mhd_assert (value_end >= value_start);

    if (delta > XBUF_SIZE - xoff)
      delta = XBUF_SIZE - xoff;
    /* move (additional) input into processing buffer */
    if (0 != delta)
    {
      memcpy (&xbuf[xoff],
              value_start,
              delta);
      xoff += delta;
      value_start += delta;
    }
    /* find if escape sequence is at the end of the processing buffer;
       if so, exclude those from processing (reduce delta to point at
       end of processed region) */
    if ( (xoff > 0) &&
         ('%' == xbuf[xoff - 1]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff--;
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        pp->xbuf[0] = '%';
        pp->xbuf_pos = 1;
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 1;
      }
    }
    else if ( (xoff > 1) &&
              ('%' == xbuf[xoff - 2]) )
    {
      cut = (xoff != XBUF_SIZE);
      xoff -= 2;
      if (cut)
      {
        /* move escape sequence into buffer for next function invocation */
        memcpy (pp->xbuf,
                &xbuf[xoff],
                2);
        pp->xbuf_pos = 2;
      }
      else
      {
        /* just skip escape sequence for next loop iteration */
        delta = xoff;
        clen = 2;
      }
    }
    mhd_assert (xoff < sizeof (xbuf));
    /* unescape */
    xbuf[xoff] = '\0';        /* 0-terminate in preparation */
    if (0 != xoff)
    {
      MHD_unescape_plus (xbuf);
      xoff = MHD_http_unescape (xbuf);
    }
    /* finally: call application! */
    if (pp->must_ikvi || (0 != xoff) )
    {
      pp->must_ikvi = false;
      if (MHD_NO == pp->ikvi (pp->cls,
                              MHD_POSTDATA_KIND,
                              (const char *) &pp[1],      /* key */
                              NULL,
                              NULL,
                              NULL,
                              xbuf,
                              pp->value_offset,
                              xoff))
      {
        pp->state = PP_Error;
        return;
      }
    }
    pp->value_offset += xoff;
    if (cut)
      break;
    if (0 != clen)
    {
      xbuf[delta] = '%';        /* undo 0-termination */
      memmove (xbuf,
               &xbuf[delta],
               clen);
    }
    xoff = clen;
  }
}
