#include <stdio.h>
#include <string.h>
#include <stddef.h>
#include <stdint.h>
#include <stdbool.h>

static int
process_value_to_boundary (struct MHD_PostProcessor *pp,
                           size_t *ioffptr,
                           const char *boundary,
                           size_t blen,
                           enum PP_State next_state,
                           enum PP_State next_dash_state)
{
  char *buf = (char *) &pp[1];
  size_t newline;
  const char *r;
  FILE *fp = fopen("/tmp/logs/634.txt", "a");

  if (fp != NULL)
  {
    fprintf(fp, "enter process_value_to_boundary: buffer_pos=%zu buffer_size=%zu value_offset=%llu must_ikvi=%d blen=%zu\n",
            pp->buffer_pos, pp->buffer_size, (unsigned long long)pp->value_offset, (int)pp->must_ikvi, blen);
  }

  /* all data in buf until the boundary
     (\r\n--+boundary) is part of the value */
  newline = 0;
  while (1)
  {
    while (newline + 4 < pp->buffer_pos)
    {
      r = memchr (&buf[newline],
                  '\r',
                  pp->buffer_pos - newline - 4);
      if (NULL == r)
      {
        newline = pp->buffer_pos - 4;
        break;
      }
      newline = (size_t) (r - buf);
      if (0 == memcmp ("\r\n--",
                       &buf[newline],
                       4))
        break;
      newline++;
    }
    if (newline + blen + 4 <= pp->buffer_pos)
    {
      /* can check boundary */
      if (0 != memcmp (&buf[newline + 4],
                       boundary,
                       blen))
      {
        /* no boundary, "\r\n--" is part of content, skip */
        if (fp != NULL)
        {
          fprintf(fp, "no boundary at pos %zu, skipping 4 -> new newline=%zu\n", newline, newline + 4);
        }
        newline += 4;
        continue;
      }
      else
      {
        /* boundary found, process until newline then
           skip boundary and go back to init */
        pp->skip_rn = RN_Dash;
        pp->state = next_state;
        pp->dash_state = next_dash_state;
        (*ioffptr) += blen + 4;             /* skip boundary as well */
        if (fp != NULL)
        {
          fprintf(fp, "boundary found at %zu: setting skip_rn=%d state=%d dash_state=%d ioffptr=%zu\n",
                  newline, (int)pp->skip_rn, (int)pp->state, (int)pp->dash_state, *ioffptr);
        }
        /* The following NUL write is used in the original code to terminate the
           buffer at the newline position. The ikvi callback receives an
           explicit length, so this write does not affect that call.
           This operation is therefore the focus of the dead-code scenario. */
        buf[newline] = '\0';
        if (fp != NULL)
        {
          fprintf(fp, "performed buf[%zu] = '\\0' (value after write=%d)\n", newline, (int)buf[newline]);
        }
        break;
      }
    }
    else
    {
      /* cannot check for boundary, process content that
         we have and check again later; except, if we have
         no content, abort (out of memory) */
      if ( (0 == newline) &&
           (pp->buffer_pos == pp->buffer_size) )
      {
        if (fp != NULL)
        {
          fprintf(fp, "out of memory condition: setting PP_Error and returning MHD_NO\n");
        }
        pp->state = PP_Error;
        if (fp != NULL)
        {
          fclose(fp);
        }
        return MHD_NO;
      }
      break;
    }
  }
  /* newline is either at beginning of boundary or
     at least at the last character that we are sure
     is not part of the boundary */
  if ( ( (pp->must_ikvi) ||
         (0 != newline) ) &&
       (MHD_NO == pp->ikvi (pp->cls,
                            MHD_POSTDATA_KIND,
                            pp->content_name,
                            pp->content_filename,
                            pp->content_type,
                            pp->content_transfer_encoding,
                            buf,
                            pp->value_offset,
                            newline)) )
  {
    if (fp != NULL)
    {
      fprintf(fp, "ikvi returned MHD_NO: setting PP_Error and returning MHD_NO\n");
      fclose(fp);
    }
    pp->state = PP_Error;
    return MHD_NO;
  }
  pp->must_ikvi = false;
  pp->value_offset += newline;
  (*ioffptr) += newline;

  if (fp != NULL)
  {
    fprintf(fp, "exit process_value_to_boundary: value_offset=%llu ioffptr=%zu must_ikvi=%d newline=%zu\n",
            (unsigned long long)pp->value_offset, *ioffptr, (int)pp->must_ikvi, newline);
    fclose(fp);
  }
  return MHD_YES;
}
