/*
 * Probe version of MHD_queue_basic_auth_required_response3 to log control path and state info
 * Logs to /tmp/logs/576.txt when the rule 1.2 is violated by use of _MHD_EXTERN
 */

/* Assuming _MHD_EXTERN is a compiler extension macro, we probe its usage */

#define _MHD_EXTERN __attribute__((visibility("default"))) /* Example extension, could cause violation */

struct MHD_Connection
{
  struct MHD_Daemon *daemon;
  /* Additional members omitted for brevity */
};

struct MHD_Response
{
  int flags;
  /* Additional members omitted for brevity */
};

enum MHD_Result { MHD_NO = 0, MHD_YES };

static size_t MHD_STATICSTR_LEN_ (const char *macro) { return strlen(macro); }

size_t MHD_str_quote (const char *unquoted, size_t unquoted_len, char *result, size_t buf_size);
enum MHD_Result MHD_add_response_header (struct MHD_Response *response, const char *header, const char *content);
enum MHD_Result MHD_queue_response (struct MHD_Connection *connection, unsigned int status_code, struct MHD_Response *response);

/* Probe function writes logs at key points indicating control flow and state */

_MHD_EXTERN enum MHD_Result
MHD_queue_basic_auth_required_response3 (struct MHD_Connection *connection,
                                         const char *realm,
                                         int prefer_utf8,
                                         struct MHD_Response *response)
{
  FILE *log_file = fopen("/tmp/logs/576.txt", "a");
  if (log_file == NULL) return MHD_NO;
  fprintf(log_file, "Entered MHD_queue_basic_auth_required_response3\n");

  static const char prefix[] = "Basic realm=\"";
  static const char suff_charset[] = "\", charset=\"UTF-8\"";
  static const size_t prefix_len = MHD_STATICSTR_LEN_ (prefix);
  static const size_t suff_simple_len = MHD_STATICSTR_LEN_ ("\"");
  static const size_t suff_charset_len = MHD_STATICSTR_LEN_ (suff_charset);
  enum MHD_Result ret;
  char *h_str;
  size_t h_maxlen;
  size_t suffix_len;
  size_t realm_len;
  size_t realm_quoted_len;
  size_t pos;

  if (NULL == response)
  {
    fprintf(log_file, "Response is NULL - returning MHD_NO\n");
    fclose(log_file);
    return MHD_NO;
  }

  suffix_len = (0 == prefer_utf8) ? suff_simple_len : suff_charset_len;
  realm_len = strlen (realm);
  h_maxlen = prefix_len + realm_len * 2 + suffix_len;
  fprintf(log_file, "Calculated h_maxlen: %zu\n", h_maxlen);

  h_str = (char *) malloc (h_maxlen + 1);
  if (NULL == h_str)
  {
    fprintf(log_file, "Memory allocation failed for h_str\n");
    fclose(log_file);
    return MHD_NO;
  }

  memcpy (h_str, prefix, prefix_len);
  pos = prefix_len;
  realm_quoted_len = MHD_str_quote (realm, realm_len, h_str + pos, h_maxlen - prefix_len - suffix_len);
  pos += realm_quoted_len;

  if (0 == prefer_utf8)
  {
    h_str[pos++] = '"';
    h_str[pos++] = 0; /* Zero terminate the result */
  }
  else
  {
    memcpy (h_str + pos, suff_charset, suff_charset_len + 1);
  }

  fprintf(log_file, "Composed WWW-Authenticate header: %s\n", h_str);

  ret = MHD_add_response_header (response, "WWW-Authenticate", h_str);
  free (h_str);

  if (MHD_NO != ret)
  {
    ret = MHD_queue_response (connection, 401, response);
    fprintf(log_file, "Queued response with status 401, ret=%d\n", ret);
  }
  else
  {
    fprintf(log_file, "Failed to add Basic Authentication header\n");
  }

  fclose(log_file);
  return ret;
}

/* Implementations of stubbed functions to keep code compilable and executable */

size_t MHD_str_quote (const char *unquoted, size_t unquoted_len, char *result, size_t buf_size)
{
  size_t r=0, w=0;
  while (r < unquoted_len && w < buf_size)
  {
    char c = unquoted[r++];
    if (c == '\\' || c == '\"')
    {
      if (w+1 >= buf_size) break;
      result[w++] = '\\';
    }
    if (w >= buf_size) break;
    result[w++] = c;
  }
  return w;
}

