static enum MHD_Result
add_response_entry (struct MHD_Response *response,
                    enum MHD_ValueKind kind,
                    const char *header,
                    const char *content)
{
  size_t header_len;
  size_t content_len;
  FILE *fp = fopen("/tmp/logs/527.txt", "a");

  if (NULL == content)
  {
    if (fp != NULL) {
      fprintf(fp, "content is NULL\n");
      fclose(fp);
    }
    return MHD_NO;
  }

  if (fp != NULL) {
    fprintf(fp, "Calculating strlen for header\n");
  }
  header_len = strlen (header);
  if (fp != NULL) {
    fprintf(fp, "header_len = %zu\n", header_len);
    fprintf(fp, "Calculating strlen for content\n");
  }
  content_len = strlen (content);
  if (fp != NULL) {
    fprintf(fp, "content_len = %zu\n", content_len);
    fclose(fp);
  }

  return add_response_entry_n (response, kind, header,
                               header_len, content,
                               content_len) ? MHD_YES : MHD_NO;
}
