#include <stdio.h>
#include <string.h>
#include <stdlib.h>

static enum MHD_Result
answer_to_connection (void *cls, struct MHD_Connection *connection,
                      const char *url, const char *method,
                      const char *version, const char *upload_data,
                      size_t *upload_data_size, void **req_cls)
{
  FILE *log = fopen("/tmp/logs/433.txt", "a");
  if (log == NULL)
  {
    /* Log file open failure ignored for MISRA compliance */
  }

  (void) cls;               /* Unused. Silent compiler warning. */
  (void) url;               /* Unused. Silent compiler warning. */
  (void) version;           /* Unused. Silent compiler warning. */

  if (log != NULL)
  {
    fprintf(log, "Entered answer_to_connection with method=%s, *upload_data_size=%zu\n", method, (unsigned long)*upload_data_size);
    fflush(log);
  }

  if (NULL == *req_cls)
  {
    struct connection_info_struct *con_info;

    con_info = malloc (sizeof (struct connection_info_struct));
    if (NULL == con_info)
    {
      if (log != NULL)
      {
        fprintf(log, "malloc returned NULL\n");
        fclose(log);
      }
      return MHD_NO;
    }
    con_info->answerstring = NULL;

    if (0 == strcmp (method, "POST"))
    {
      if (log != NULL)
      {
        fprintf(log, "Setting up postprocessor and type POST\n");
        fflush(log);
      }
      con_info->postprocessor =
        MHD_create_post_processor (connection, POSTBUFFERSIZE,
                                   iterate_post, (void *) con_info);

      if (NULL == con_info->postprocessor)
      {
        free (con_info);
        if (log != NULL)
        {
          fprintf(log, "postprocessor creation failed\n");
          fclose(log);
        }
        return MHD_NO;
      }

      con_info->connectiontype = POST;
    }
    else
    {
      if (log != NULL)
      {
        fprintf(log, "Connection type set to GET\n");
        fflush(log);
      }
      con_info->connectiontype = GET;
    }

    *req_cls = (void *) con_info;

    if (log != NULL)
      fclose(log);

    return MHD_YES;
  }

  if (0 == strcmp (method, "GET"))
  {
    if (log != NULL)
    {
      fprintf(log, "Handle GET method, calling send_page with askpage\n");
      fclose(log);
    }
    return send_page (connection, askpage);
  }

  if (0 == strcmp (method, "POST"))
  {
    struct connection_info_struct *con_info = *req_cls;

    if (*upload_data_size != 0)
    {
      if (log != NULL)
      {
        fprintf(log, "upload_data_size != 0, calling MHD_post_process\n");
        fflush(log);
      }

      if (MHD_YES !=
          MHD_post_process (con_info->postprocessor,
                            upload_data,
                            *upload_data_size))
      {
        if (log != NULL)
        {
          fprintf(log, "MHD_post_process failed, returning MHD_NO\n");
          fclose(log);
        }
        return MHD_NO;
      }
      *upload_data_size = 0;

      if (log != NULL)
      {
        fprintf(log, "Processed upload data, returning MHD_YES\n");
        fclose(log);
      }

      return MHD_YES;
    }
    else if (NULL != con_info->answerstring)
    {
      if (log != NULL)
      {
        fprintf(log, "upload_data_size == 0 and answerstring not NULL, sending page\n");
        fclose(log);
      }
      return send_page (connection, con_info->answerstring);
    }
  }

  if (log != NULL)
  {
    fprintf(log, "Default case: returning errorpage\n");
    fclose(log);
  }

  return send_page (connection, errorpage);
}
