#include <stdio.h>
#include <string.h>
#include <stdlib.h>

struct connection_info_struct
{
  int connectiontype;
  char *answerstring;
  struct MHD_PostProcessor *postprocessor;
};

enum MHD_Result { MHD_NO=0, MHD_YES=1 };

static enum MHD_Result
send_page (void *connection, const char *page)
{
  FILE *f = fopen("/tmp/logs/435.txt", "a");
  if(f)
  {
    fprintf(f, "send_page called with page: %s\n", page);
    fclose(f);
  }
  return MHD_YES;
}

static enum MHD_Result
MHD_post_process (struct MHD_PostProcessor *pp, char *post_data, size_t post_data_len)
{
  FILE *f = fopen("/tmp/logs/435.txt", "a");
  if(f)
  {
    fprintf(f, "MHD_post_process called with data (size %zu): %.*s\n", post_data_len, (int)post_data_len, post_data);
    fclose(f);
  }
  /* Pretend to modify data to indicate modification */
  if(post_data_len > 0)
    post_data[0] = 'X';
  return MHD_YES;
}

static enum MHD_Result
answer_to_connection (void *cls, void *connection,
                      const char *url, const char *method,
                      const char *version, const char *upload_data,
                      size_t *upload_data_size, void **req_cls)
{
  (void) cls;
  (void) url;
  (void) version;

  if (NULL == *req_cls)
  {
    struct connection_info_struct *con_info;

    con_info = malloc (sizeof (struct connection_info_struct));
    if (NULL == con_info)
      return MHD_NO;
    con_info->answerstring = NULL;

    if (0 == strcmp (method, "POST"))
    {
      con_info->postprocessor = NULL; /* Simplified for test */
      con_info->connectiontype = 1; /* POST */
    }
    else
      con_info->connectiontype = 0; /* GET */

    *req_cls = (void *) con_info;

    return MHD_YES;
  }

  if (0 == strcmp (method, "GET"))
  {
    return send_page (connection, "askpage");
  }

  if (0 == strcmp (method, "POST"))
  {
    struct connection_info_struct *con_info = *req_cls;

    if (*upload_data_size != 0)
    {
      FILE *f = fopen("/tmp/logs/435.txt", "a");
      if(f)
      {
        fprintf(f, "Processing POST data: size=%zu\n", *upload_data_size);
        fclose(f);
      }
      /* Violation point: cast away const to match MHD_post_process expected param type */
      if (MHD_YES !=
          MHD_post_process (con_info->postprocessor,
                            (char *)upload_data,
                            *upload_data_size))
        return MHD_NO;
      *upload_data_size = 0;

      return MHD_YES;
    }
    else if (NULL != con_info->answerstring)
      return send_page (connection, con_info->answerstring);
  }

  return send_page (connection, "errorpage");
}
