#include <stdio.h>
#include <string.h>
#include <stdint.h>
#include <stdbool.h>

#define MAX_DIGEST 64
#define NONCE_STD_LEN(digest_size) ((digest_size) + 10)  /* Hypothetical macro adding 10 bytes */

struct DigestAlgorithm {
  bool hashing;
  bool uninitialised;
  bool algo_selected;
  int algo;
};

struct MHD_Connection {
  struct MHD_Daemon *daemon;
  struct {
    int http_mthd;
    int method;
    const char *url;
    size_t url_len;
    size_t headers_received;
  } rq;
  const void *addr;
  int addr_len;
};

struct MHD_Daemon {
  int digest_auth_rand_size;
  char *digest_auth_random;
  int nonce_nc_size;
  struct MHD_NonceNc *nnc;
  int nnc_lock;
  int dauth_bind_type;
};

struct MHD_NonceNc {
  char nonce[MAX_DIGEST + 11];
  int nc;
  int nmask;
};

static void mhd_assert(bool condition) {
  if (!condition) {
    FILE *f = fopen("/tmp/logs/599.txt", "a");
    if (f) {
      fprintf(f, "ASSERT FAILED\n");
      fclose(f);
    }
  }
}

size_t strlen(const char *s);

static unsigned int digest_get_size(struct DigestAlgorithm *da) {
  // Intentionally return a large size to cause oversized memcpy length
  return 64; // deliberately large
}

static bool calculate_add_nonce(struct MHD_Connection *const connection,
                                uint64_t timestamp,
                                const char *realm,
                                size_t realm_len,
                                struct DigestAlgorithm *da,
                                char *nonce) {
  return true;
}

static uint64_t MHD_monotonic_msec_counter(void) {
  return 1234567890ULL;
}

void log_state(const char *msg) {
  FILE *f = fopen("/tmp/logs/599.txt", "a");
  if (f) {
    fprintf(f, "%s\n", msg);
    fclose(f);
  }
}

static bool calculate_add_nonce_with_retry(struct MHD_Connection *const connection,
                                           const char *realm,
                                           struct DigestAlgorithm *da,
                                           char *nonce) {
  const uint64_t timestamp1 = MHD_monotonic_msec_counter();
  const size_t realm_len = strlen(realm);
  mhd_assert(!da->hashing);

  log_state("Entered calculate_add_nonce_with_retry");

  if (!calculate_add_nonce(connection, timestamp1, realm, realm_len, da, nonce)) {
    const size_t digest_size = digest_get_size(da);
    char nonce2[NONCE_STD_LEN(MAX_DIGEST) + 1];
    uint64_t timestamp2 = timestamp1 + 1;

    log_state("calculate_add_nonce failed initially, retrying with nonce2");

    if (!calculate_add_nonce(connection, timestamp2, realm, realm_len, da, nonce2)) {
      log_state("calculate_add_nonce failed on retry");
      return false;
    }

    /* Intentional unsafe memcpy to demonstrate potential buffer overflow */
    log_state("About to memcpy nonce2 to nonce with length exceeding nonce buffer");
    memcpy(nonce, nonce2, NONCE_STD_LEN(digest_size));
    log_state("memcpy completed");
  }
  return true;
}
