#include <stdio.h>
#include <string.h>
#include <windows.h>

static int
mhd_tool_get_sys_cpu_count_special_api_ (void)
{
  int ret = -1;
  FILE *log = fopen("/tmp/logs/296.txt", "w");
  if (log == NULL)
    return -1;
#ifdef HAVE_PSTAT_GETDYNAMIC
  if (0 >= ret)
  {
    fprintf(log, "Checking PSTAT_GETDYNAMIC path\n");
    struct pst_dynamic psd_data;
    memset ((void *) &psd_data, 0, sizeof (psd_data));
    if (1 == pstat_getdynamic (&psd_data, sizeof (psd_data), (size_t) 1, 0))
    {
      if (0 < psd_data.psd_proc_cnt)
      {
        ret = (int) psd_data.psd_proc_cnt;
        fprintf(log, "PSTAT proc count: %d\n", ret);
      }
    }
  }
#endif /* HAVE_PSTAT_GETDYNAMIC */
#ifdef HAVE_VXCPUENABLEDGET
  if (0 >= ret)
  {
    fprintf(log, "Checking VxWorks cpu enabled path\n");
    cpuset_t enb_set;
    enb_set = vxCpuEnabledGet ();
    for (ret = 0; 0 != enb_set; enb_set &= enb_set - 1)
      ++ret;
    fprintf(log, "VxWorks cpu count: %d\n", ret);
  }
#endif /* HAVE_VXCPUENABLEDGET */
#if defined(_WIN32) && ! defined (__CYGWIN__)
  if (0 >= ret)
  {
    fprintf(log, "Entering Windows API dynamic loading path\n");
    HMODULE k32hndl;
    k32hndl = LoadLibraryA ("kernel32.dll");
    fprintf(log, "LoadLibraryA kernel32.dll returned: %p\n", (void *)k32hndl);
    if (NULL != k32hndl)
    {
      typedef DWORD (WINAPI *GAPC_PTR)(WORD GroupNumber);
      GAPC_PTR ptrGetActiveProcessorCount;
      ptrGetActiveProcessorCount =
        (GAPC_PTR) (void *) GetProcAddress (k32hndl, "GetActiveProcessorCount");
      fprintf(log, "GetProcAddress GetActiveProcessorCount returned: %p\n", (void *)ptrGetActiveProcessorCount);
      if (NULL != ptrGetActiveProcessorCount)
      {
        DWORD res;
        res = ptrGetActiveProcessorCount (ALL_PROCESSOR_GROUPS);
        ret = (int) res;
        fprintf(log, "GetActiveProcessorCount result: %u, ret: %d\n", res, ret);
        if (res != (DWORD) ret)
          ret = -1; /* Overflow */
      }
    }
    if ((0 >= ret) && (NULL != k32hndl))
    {
      typedef void (WINAPI *GNSI_PTR)(SYSTEM_INFO *pSysInfo);
      GNSI_PTR ptrGetNativeSystemInfo;
      ptrGetNativeSystemInfo =
        (GNSI_PTR) (void *) GetProcAddress (k32hndl, "GetNativeSystemInfo");
      fprintf(log, "GetProcAddress GetNativeSystemInfo returned: %p\n", (void *)ptrGetNativeSystemInfo);
      if (NULL != ptrGetNativeSystemInfo)
      {
        SYSTEM_INFO sysInfo;

        memset ((void *) &sysInfo, 0, sizeof (sysInfo));
        ptrGetNativeSystemInfo (&sysInfo);
        ret = (int) sysInfo.dwNumberOfProcessors;
        fprintf(log, "GetNativeSystemInfo processor count: %u, ret: %d\n", sysInfo.dwNumberOfProcessors, ret);
        if (sysInfo.dwNumberOfProcessors != (DWORD) ret)
          ret = -1; /* Overflow */
      }
    }
    if (NULL != k32hndl)
    {
      FreeLibrary (k32hndl);
      fprintf(log, "FreeLibrary called\n");
    }
  }
  if (0 >= ret)
  {
    SYSTEM_INFO sysInfo;
    memset ((void *) &sysInfo, 0, sizeof (sysInfo));
    GetSystemInfo (&sysInfo);
    ret = (int) sysInfo.dwNumberOfProcessors;
    fprintf(log, "GetSystemInfo processor count: %u, ret: %d\n", sysInfo.dwNumberOfProcessors, ret);
    if (sysInfo.dwNumberOfProcessors != (DWORD) ret)
      ret = -1; /* Overflow */
  }
#endif /* _WIN32 && ! __CYGWIN__ */
  fclose(log);
  if (0 >= ret)
    return -1;
  return ret;
}
