#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <sys/socket.h>
#include <pthread.h>

extern pthread_mutex_t MUTEX;
extern int CLIENT_SOCKS[];
extern int MAX_CLIENTS;
extern int MHD_INVALID_SOCKET;
extern void MHD_upgrade_action(void *urh, int action);

// Log function to trace control flow and state
static void log_to_file(const char *msg)
{
  FILE *f = fopen("/tmp/logs/207.txt", "a");
  if (f != NULL)
  {
    fprintf(f, "%s\n", msg);
    fclose(f);
  }
}

static void * run_usock (void *cls)
{
  struct WsData *ws = cls;
  struct MHD_UpgradeResponseHandle *urh = ws->urh;
  unsigned char buf[2048];
  unsigned char *msg;
  char *text;
  ssize_t got;
  int type;
  int i;

  log_to_file("Entered run_usock");

  make_blocking (ws->sock);
  while (1)
  {
    got = recv (ws->sock, (void *) buf, sizeof (buf), 0);
    char logbuf[128];
    snprintf(logbuf, sizeof(logbuf), "recv returned %zd bytes", got);
    log_to_file(logbuf);

    if (0 >= got)
    {
      log_to_file("recv <= 0, breaking loop");
      break;
    }

    msg = ws_receive_frame (buf, &got, &type);
    if (NULL == msg)
    {
      log_to_file("ws_receive_frame returned NULL, breaking loop");
      break;
    }

    snprintf(logbuf, sizeof(logbuf), "Received frame type: %d, length: %zd", type, got);
    log_to_file(logbuf);

    if (type == WS_OPCODE_TEXT_FRAME)
    {
      ssize_t sent;
      int buf_size;
      buf_size = snprintf (NULL, 0, "User#%d: %s", (int) ws->sock, msg);
      log_to_file("Formatting user message");

      if (0 < buf_size)
      {
        text = malloc ((size_t) buf_size + 1);
        if (NULL != text)
        {
          if (snprintf (text, (size_t) buf_size + 1,
                        "User#%d: %s", (int) ws->sock, msg) == buf_size)
            sent = ws_send_frame (ws->sock, text, (size_t) buf_size);
          else
            sent = -1;

          snprintf(logbuf, sizeof(logbuf), "Sent %zd bytes", sent);
          log_to_file(logbuf);
          free (text);
        }
        else
          sent = -1;
      }
      else
        sent = -1;

      free (msg);

      if (-1 == sent)
      {
        log_to_file("Send failed, breaking loop");
        break;
      }
    }
    else
    {
      if (type == WS_OPCODE_CON_CLOSE_FRAME)
      {
        log_to_file("Received close frame, breaking loop");
        free (msg);
        break;
      }
    }
  }

  if (0 != pthread_mutex_lock (&MUTEX))
    abort ();
  log_to_file("Locked mutex for client cleanup");
  for (i = 0; i < MAX_CLIENTS; i++)
  {
    if (CLIENT_SOCKS[i] == ws->sock)
    {
      CLIENT_SOCKS[i] = MHD_INVALID_SOCKET;
      break;
    }
  }
  if (0 != pthread_mutex_unlock (&MUTEX))
    abort ();
  log_to_file("Unlocked mutex and freeing ws");
  free (ws);
  MHD_upgrade_action (urh, MHD_UPGRADE_ACTION_CLOSE);
  log_to_file("Exiting run_usock");
  return NULL;
}
