static ssize_t
send_iov_nontls (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
  ssize_t res;
  size_t items_to_send;
#ifdef HAVE_SENDMSG
  struct msghdr msg;
#elif defined(MHD_WINSOCK_SOCKETS)
  DWORD bytes_sent;
  DWORD cnt_w;
#endif /* MHD_WINSOCK_SOCKETS */

  FILE *log_file = fopen("/tmp/logs/641.txt", "a");
  if (log_file != NULL)
  {
    fprintf(log_file, "send_iov_nontls: start\n");
  }

  mhd_assert (0 == (connection->daemon->options & MHD_USE_TLS));

  if ( (MHD_INVALID_SOCKET == connection->socket_fd) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    if (log_file != NULL)
    {
      fprintf(log_file, "send_iov_nontls: invalid socket or connection closed\n");
      fclose(log_file);
    }
    return MHD_ERR_NOTCONN_;
  }

  items_to_send = r_iov->cnt - r_iov->sent;
#ifdef _MHD_IOV_MAX
  if (_MHD_IOV_MAX < items_to_send)
  {
    mhd_assert (0 < _MHD_IOV_MAX);
    if (0 == _MHD_IOV_MAX)
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: _MHD_IOV_MAX is zero, returning error\n");
        fclose(log_file);
      }
      return MHD_ERR_NOTCONN_; /* Should never happen */
    }
    items_to_send = _MHD_IOV_MAX;
    push_data = false; /* Incomplete response */
    if (log_file != NULL)
    {
      fprintf(log_file, "send_iov_nontls: limited items_to_send to _MHD_IOV_MAX and push_data set to false\n");
    }
  }
#endif /* _MHD_IOV_MAX */

#ifdef HAVE_SENDMSG
  memset (&msg, 0, sizeof(struct msghdr));
  msg.msg_iov = r_iov->iov + r_iov->sent;
  msg.msg_iovlen = items_to_send;

  pre_send_setopt (connection, true, push_data);
#ifdef MHD_USE_MSG_MORE
  res = sendmsg (connection->socket_fd, &msg,
                 MSG_NOSIGNAL_OR_ZERO | (push_data ? 0 : MSG_MORE));
#else  /* ! MHD_USE_MSG_MORE */
  res = sendmsg (connection->socket_fd, &msg, MSG_NOSIGNAL_OR_ZERO);
#endif /* ! MHD_USE_MSG_MORE */
#elif defined(HAVE_WRITEV)
  pre_send_setopt (connection, true, push_data);
  res = writev (connection->socket_fd, r_iov->iov + r_iov->sent,
                items_to_send);
#elif defined(MHD_WINSOCK_SOCKETS)
#ifdef _WIN64
  if (items_to_send > UINT32_MAX)
  {
    cnt_w = UINT32_MAX;
    push_data = false; /* Incomplete response */
  }
  else
    cnt_w = (DWORD) items_to_send;
#else  /* ! _WIN64 */
  cnt_w = (DWORD) items_to_send;
#endif /* ! _WIN64 */
  pre_send_setopt (connection, true, push_data);
  if (0 == WSASend (connection->socket_fd,
                    (LPWSABUF) (r_iov->iov + r_iov->sent),
                    cnt_w,
                    &bytes_sent, 0, NULL, NULL))
    res = (ssize_t) bytes_sent;
  else
    res = -1;
#else /* !HAVE_SENDMSG && !HAVE_WRITEV && !MHD_WINSOCK_SOCKETS */
#error No vector-send function available
#endif

  if (0 > res)
  {
    const int err = MHD_socket_get_error_ ();

    if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
    {
#ifdef EPOLL_SUPPORT
      /* EAGAIN --- no longer write-ready */
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: EAGAIN error, returning MHD_ERR_AGAIN_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: EINTR error, returning MHD_ERR_AGAIN_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Remote disconnect error, returning MHD_ERR_CONNRESET_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_CONNRESET_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: EPIPE error, returning MHD_ERR_PIPE_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_PIPE_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Operation not supported error, returning MHD_ERR_OPNOTSUPP_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_OPNOTSUPP_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Not connected error, returning MHD_ERR_NOTCONN_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_NOTCONN_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Invalid argument error, returning MHD_ERR_INVAL_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_INVAL_;
    }
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Low resources error, returning MHD_ERR_NOMEM_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_NOMEM_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Bad file descriptor error, returning MHD_ERR_BADF_\n");
      }
      if (log_file != NULL) fclose(log_file);
      return MHD_ERR_BADF_;
    }
    /* Treat any other error as a hard error. */
    if (log_file != NULL)
    {
      fprintf(log_file, "send_iov_nontls: Unhandled error code, returning MHD_ERR_NOTCONN_\n");
    }
    if (log_file != NULL) fclose(log_file);
    return MHD_ERR_NOTCONN_;
  }

  /* Some data has been sent */
  if (1)
  {
    size_t track_sent = (size_t) res;
    if (log_file != NULL)
    {
      fprintf(log_file, "send_iov_nontls: Entering always-true if(1) block, res=%zd\n", res);
    }
    /* Adjust the internal tracking information for the iovec to
     * take this last send into account. */
    while ((0 != track_sent) && (r_iov->iov[r_iov->sent].iov_len <= track_sent))
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: Advancing sent index=%zu, track_sent=%zu\n", r_iov->sent, track_sent);
      }
      track_sent -= r_iov->iov[r_iov->sent].iov_len;
      r_iov->sent++; /* The iov element has been completely sent */
      mhd_assert ((r_iov->cnt > r_iov->sent) || (0 == track_sent));
    }

    if (r_iov->cnt == r_iov->sent)
    {
      if (log_file != NULL)
      {
        fprintf(log_file, "send_iov_nontls: All iovecs sent, calling post_send_setopt\n");
      }
      post_send_setopt (connection, true, push_data);
    }
    else
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      if (0 != track_sent)
      {
        mhd_assert (r_iov->cnt > r_iov->sent);
        /* The last iov element has been partially sent */
        r_iov->iov[r_iov->sent].iov_base =
          (void *) ((uint8_t *) r_iov->iov[r_iov->sent].iov_base + track_sent);
        r_iov->iov[r_iov->sent].iov_len -= (MHD_iov_size_) track_sent;
        if (log_file != NULL)
        {
          fprintf(log_file, "send_iov_nontls: Partially sent iov %zu, adjusted pointers and length\n", r_iov->sent);
        }
      }
    }
  }
  else
  {
    /* This else branch is unreachable code as the if condition is always true */
    if (log_file != NULL)
    {
      fprintf(log_file, "send_iov_nontls: Unreachable else branch accessed\n");
    }
  }

  if (log_file != NULL)
  {
    fprintf(log_file, "send_iov_nontls: end, returning res=%zd\n", res);
    fclose(log_file);
  }

  return res;
}
