#include <stdio.h>
#include <stdbool.h>

static void lfs_probe_log(const char *event,
        unsigned long head, unsigned long index, int count,
        unsigned long h0, unsigned long h1) {
    FILE *f = fopen("/tmp/logs/10.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "%s: head=%lu index=%lu count=%d h0=%lu h1=%lu\n",
            event, head, index, count, h0, h1);
    fclose(f);
}

static int lfs_ctz_traverse(lfs_t *lfs,
        const lfs_cache_t *pcache, lfs_cache_t *rcache,
        lfs_block_t head, lfs_size_t size,
        int (*cb)(void*, lfs_block_t), void *data) {
    if (size == 0) {
        lfs_probe_log("lfs_ctz_traverse:size==0", (unsigned long)head, 0UL, 0, 0UL, 0UL);
        return 0;
    }

    lfs_off_t index = lfs_ctz_index(lfs, &(lfs_off_t){size-1});
    lfs_probe_log("lfs_ctz_traverse:after_index", (unsigned long)head, (unsigned long)index, 0, 0UL, 0UL);

    while (true) {
        lfs_probe_log("lfs_ctz_traverse:before_cb", (unsigned long)head, (unsigned long)index, 0, 0UL, 0UL);
        int err = cb(data, head);
        if (err) {
            lfs_probe_log("lfs_ctz_traverse:cb_err", (unsigned long)head, (unsigned long)index, 0, 0UL, 0UL);
            return err;
        }

        if (index == 0) {
            lfs_probe_log("lfs_ctz_traverse:done_index0", (unsigned long)head, (unsigned long)index, 0, 0UL, 0UL);
            return 0;
        }

        lfs_block_t heads[2];
        int count = 2 - (index & 1);
        err = lfs_bd_read(lfs,
                pcache, rcache, count*sizeof(head),
                head, 0, &heads, count*sizeof(head));
        /* preserve original behavior: convert both entries unconditionally */
        heads[0] = lfs_fromle32(heads[0]);
        heads[1] = lfs_fromle32(heads[1]);
        lfs_probe_log("lfs_ctz_traverse:after_read_and_fromle32",
                (unsigned long)head, (unsigned long)index, count,
                (unsigned long)heads[0], (unsigned long)heads[1]);
        if (err) {
            lfs_probe_log("lfs_ctz_traverse:read_err", (unsigned long)head, (unsigned long)index, count,
                    (unsigned long)heads[0], (unsigned long)heads[1]);
            return err;
        }

        for (int i = 0; i < count-1; i++) {
            lfs_probe_log("lfs_ctz_traverse:before_cb_heads", (unsigned long)head, (unsigned long)index, count,
                    (unsigned long)heads[0], (unsigned long)heads[1]);
            err = cb(data, heads[i]);
            if (err) {
                lfs_probe_log("lfs_ctz_traverse:cb_err_heads", (unsigned long)head, (unsigned long)index, count,
                        (unsigned long)heads[0], (unsigned long)heads[1]);
                return err;
            }
        }

        /* log potential dead operation: when count == 1, heads[1] was still converted above
         * but is not used afterwards. This is the scenario demonstrating the dead operation. */
        if (count == 1) {
            lfs_probe_log("lfs_ctz_traverse:dead_op_observed", (unsigned long)head, (unsigned long)index, count,
                    (unsigned long)heads[0], (unsigned long)heads[1]);
        }

        head = heads[count-1];
        index -= count;
    }
}
