#include <stdio.h>
#include <stdint.h>

/* Probe helpers: append human-readable control-path and state information
   to /tmp/logs/50.txt. These helpers preserve the original function logic
   and only add logging side-effects to illustrate the dead-branch scenario. */
static void probe_log_enter(const void *lfs, const void *file, const void *buffer, unsigned long size) {
    FILE *fp = fopen("/tmp/logs/50.txt", "a");
    if (fp) {
        fprintf(fp, "ENTER: lfs=%p file=%p buffer=%p size=%lu\n", lfs, file, buffer, size);
        fclose(fp);
    }
}

static void probe_log_lock(int err) {
    FILE *fp = fopen("/tmp/logs/50.txt", "a");
    if (fp) {
        fprintf(fp, "LOCK returned: %d\n", err);
        fclose(fp);
    }
}

static void probe_log_return_err(int err) {
    FILE *fp = fopen("/tmp/logs/50.txt", "a");
    if (fp) {
        fprintf(fp, "EARLY RETURN: err=%d\n", err);
        fclose(fp);
    }
}

static void probe_log_before_call(void) {
    FILE *fp = fopen("/tmp/logs/50.txt", "a");
    if (fp) {
        fprintf(fp, "Calling lfs_file_read_()\n");
        fclose(fp);
    }
}

static void probe_log_after_call(long res) {
    FILE *fp = fopen("/tmp/logs/50.txt", "a");
    if (fp) {
        fprintf(fp, "RETURN: res=%ld\n", res);
        fclose(fp);
    }
}

/* Preserve original signature and logic; add probes only. */
lfs_ssize_t lfs_file_read(lfs_t *lfs, lfs_file_t *file,
        void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    /* Log the lock return value. In the provided project configuration
       LFS_LOCK(...) expands to '((void)cfg,0)' so err is always 0. */
    probe_log_lock(err);
    if (err) {
        /* This branch is never taken with the current LFS_LOCK definition.
           We log it to demonstrate the dead-branch scenario if it ever
           becomes reachable with a different configuration. */
        probe_log_return_err(err);
        return err;
    }

    probe_log_enter((const void*)lfs, (const void*)file, buffer, (unsigned long)size);

    LFS_TRACE("lfs_file_read(%p, %p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, buffer, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    probe_log_before_call();
    lfs_ssize_t res = lfs_file_read_(lfs, file, buffer, size);
    probe_log_after_call((long)res);

    LFS_TRACE("lfs_file_read -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
