/* Probe helper that appends control-path/state information to /tmp/logs/42.txt */
static void probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/42.txt", "a");
    if (f == NULL) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

