#include <stdio.h>
#include <inttypes.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/138.txt */
static void lfs_probe_log(const char *stage, lfs_t *lfs, lfs_file_t *file, lfs_off_t size, int err) {
    FILE *f = fopen("/tmp/logs/138.txt", "a");
    if (f) {
        fprintf(f, "%s: lfs=%p file=%p size=%" PRIu32 " err=%d\n",
                stage, (void*)lfs, (void*)file, (uint32_t)size, err);
        fclose(f);
    }
}

int lfs_file_truncate(lfs_t *lfs, lfs_file_t *file, lfs_off_t size) {
    /* Log entry and state before locking */
    lfs_probe_log("enter", lfs, file, size, 0);

    int err = LFS_LOCK(lfs->cfg);
    /* Log result of lock attempt */
    lfs_probe_log("after_lock", lfs, file, size, err);

    if (err) {
        /* Log the (theoretically) early return on lock failure */
        lfs_probe_log("lock_failed_return", lfs, file, size, err);
        return err;
    }

    LFS_TRACE("lfs_file_truncate(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_truncate_(lfs, file, size);

    /* Log outcome from the internal truncate operation */
    lfs_probe_log("after_truncate_internal", lfs, file, size, err);

    LFS_TRACE("lfs_file_truncate -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Log after unlock (demonstrates the no-op unlock in the provided config) */
    lfs_probe_log("after_unlock", lfs, file, size, err);

    return err;
}
