#include <stdio.h>

/* Probe function: append control-path and state info to /tmp/logs/106.txt */
static void lfs_log(const char *msg, const lfs_t *lfs, const struct lfs_config *cfg, int err) {
    FILE *f = fopen("/tmp/logs/106.txt", "a");
    if (f != NULL) {
        fprintf(f, "%s: lfs=%p cfg=%p err=%d\n", msg, (const void*)lfs, (const void*)cfg, err);
        fclose(f);
    }
}

int lfs_format(lfs_t *lfs, const struct lfs_config *cfg) {
    int err = LFS_LOCK(cfg);
    /* Log state right after attempting lock */
    lfs_log("after LFS_LOCK", lfs, cfg, err);

    if (err) {
        lfs_log("return on LFS_LOCK error", lfs, cfg, err);
        return err;
    }

    LFS_TRACE("lfs_format(%p, %p {.context=%p, "
                ".read=%p, .prog=%p, .erase=%p, .sync=%p, "
                ".read_size=%"PRIu32", .prog_size=%"PRIu32", "
                ".block_size=%"PRIu32", .block_count=%"PRIu32", "
                ".block_cycles=%"PRId32", .cache_size=%"PRIu32", "
                ".lookahead_size=%"PRIu32", .read_buffer=%p, "
                ".prog_buffer=%p, .lookahead_buffer=%p, "
                ".name_max=%"PRIu32", .file_max=%"PRIu32", "
                ".attr_max=%"PRIu32"})",
            (void*)lfs, (void*)cfg, cfg->context,
            (void*)(uintptr_t)cfg->read, (void*)(uintptr_t)cfg->prog,
            (void*)(uintptr_t)cfg->erase, (void*)(uintptr_t)cfg->sync,
            cfg->read_size, cfg->prog_size, cfg->block_size, cfg->block_count,
            cfg->block_cycles, cfg->cache_size, cfg->lookahead_size,
            cfg->read_buffer, cfg->prog_buffer, cfg->lookahead_buffer,
            cfg->name_max, cfg->file_max, cfg->attr_max);

    /* Log right before invoking the formatting implementation */
    lfs_log("before lfs_format_", lfs, cfg, -1);

    err = lfs_format_(lfs, cfg);

    /* Log result from lfs_format_ */
    lfs_log("after lfs_format_", lfs, cfg, err);

    LFS_TRACE("lfs_format -> %d", err);
    LFS_UNLOCK(cfg);

    /* Log after unlock to show that unlock is a no-op in this configuration */
    lfs_log("after LFS_UNLOCK", lfs, cfg, err);

    return err;
}
