#include <stdio.h>

int lfs_fs_mkconsistent(lfs_t *lfs) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and the result of the lock operation */
    {
        FILE *f = fopen("/tmp/logs/90.txt", "a");
        if (f) {
            fprintf(f, "enter lfs_fs_mkconsistent: LFS_LOCK returned %d, lfs=%p\n", err, (void*)lfs);
            fclose(f);
        }
    }

    if (err) {
        return err;
    }

    LFS_TRACE("lfs_fs_mkconsistent(%p)", (void*)lfs);

    err = lfs_fs_mkconsistent_(lfs);

    /* Probe: log return from inner consistency operation and note on unlock */
    {
        FILE *f = fopen("/tmp/logs/90.txt", "a");
        if (f) {
            fprintf(f, "after lfs_fs_mkconsistent_: err=%d\n", err);
            fprintf(f, "about to call LFS_UNLOCK(lfs->cfg) (may be a no-op).\n");
            fclose(f);
        }
    }

    LFS_TRACE("lfs_fs_mkconsistent -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
