#include <stdio.h>
#include <inttypes.h>

static void lfs_probe_log(const char *stage, lfs_t *lfs, lfs_ssize_t res, int err) {
    FILE *f = fopen("/tmp/logs/178.txt", "a");
    if (f) {
        /* Log control path and key state information */
        fprintf(f, "%s: lfs=%p err=%d res=%" PRId32 "\n",
                stage, (void*)lfs, err, res);
        fclose(f);
    }
}

lfs_ssize_t lfs_fs_size(lfs_t *lfs) {
    int err = LFS_LOCK(lfs->cfg);
    /* Probe after lock to show the lock result (in the provided config this is 0) */
    lfs_probe_log("after_lock", lfs, 0, err);

    if (err) {
        lfs_probe_log("lock_failed", lfs, 0, err);
        return err;
    }

    LFS_TRACE("lfs_fs_size(%p)", (void*)lfs);

    lfs_ssize_t res = lfs_fs_size_(lfs);

    lfs_probe_log("after_size_", lfs, res, 0);
    LFS_TRACE("lfs_fs_size -> %" PRId32, res);

    LFS_UNLOCK(lfs->cfg);
    /* Probe after unlock to show unlock point was reached */
    lfs_probe_log("after_unlock", lfs, res, 0);

    return res;
}
