int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log control/state before checking err */
    {
        FILE *fp = fopen("/tmp/logs/185.txt", "a");
        if (fp) {
            fprintf(fp, "lfs_fs_stat: entry lfs=%p fsinfo=%p LFS_LOCK returned err=%d\n",
                    (void*)lfs, (void*)fsinfo, err);
            fclose(fp);
        }
    }

    if (err) {
        /* Probe: this branch is unreachable in the present project because
         * LFS_LOCK is defined as ((void)cfg,0). If this branch were reachable
         * it would log and return the error code. */
        FILE *fp = fopen("/tmp/logs/185.txt", "a");
        if (fp) {
            fprintf(fp, "lfs_fs_stat: unreachable branch taken, returning %d\n", err);
            fclose(fp);
        }
        return err;
    }

    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);

    err = lfs_fs_stat_(lfs, fsinfo);

    /* Probe: log result after calling lfs_fs_stat_ */
    {
        FILE *fp = fopen("/tmp/logs/185.txt", "a");
        if (fp) {
            fprintf(fp, "lfs_fs_stat: lfs_fs_stat_ returned %d (lfs=%p fsinfo=%p)\n",
                    err, (void*)lfs, (void*)fsinfo);
            fclose(fp);
        }
    }

    LFS_TRACE("lfs_fs_stat -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
