lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }
    LFS_TRACE("lfs_getattr(%p, \"%s\", %"PRIu8", %p, %"PRIu32")",
            (void*)lfs, path, type, buffer, size);

    /* Probe logging: record control path/state relevant to comparisons against
       "." and ".." which in the original code are performed with memcmp.
       The probe uses only standard string operations and does not introduce
       new uses of memcmp or other MISRA violations. */
    {
        FILE *fp = fopen("/tmp/logs/206.txt", "a");
        if (fp != NULL) {
            const char *pp = path;
            if (pp == NULL) {
                fprintf(fp, "lfs_getattr probe: path=(null), type=%u, buffer=%p, size=%lu\n",
                        (unsigned)type, buffer, (unsigned long)size);
            } else {
                fprintf(fp, "lfs_getattr probe: path=\"%s\", type=%u, buffer=%p, size=%lu\n",
                        pp, (unsigned)type, buffer, (unsigned long)size);
            }

            /* Walk components like lfs_dir_find to detect components that would
               trigger the memcmp checks for "." and "..". Log offset, index
               and component length to help reproduce the violation scenario. */
            if (pp == NULL) {
                fprintf(fp, "  probe: path is NULL\n");
            } else if (*pp == '\0') {
                fprintf(fp, "  probe: path is empty\n");
            } else {
                unsigned comp_index = 0u;
                const char *cur = pp;
                while (*cur != '\0') {
                    /* skip slashes */
                    while (*cur == '/') {
                        cur++;
                    }
                    if (*cur == '\0') {
                        break;
                    }
                    const char *start = cur;
                    while (*cur != '/' && *cur != '\0') {
                        cur++;
                    }
                    lfs_size_t namelen = (lfs_size_t)(cur - start);
                    comp_index++;

                    if (namelen == 1u && start[0] == '.') {
                        fprintf(fp, "  component %u at offset %ld is \".\" -> memcmp(name, \".\", 1) would be used\n",
                                comp_index, (long)(start - pp));
                    } else if (namelen == 2u && start[0] == '.' && start[1] == '.') {
                        fprintf(fp, "  component %u at offset %ld is \"..\" -> memcmp(name, \"..\", 2) would be used\n",
                                comp_index, (long)(start - pp));
                    } else {
                        fprintf(fp, "  component %u at offset %ld len=%lu\n",
                                comp_index, (long)(start - pp), (unsigned long)namelen);
                    }
                }
            }
            fclose(fp);
        }
    }

    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    LFS_TRACE("lfs_getattr -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    return res;
}
