#include <stdio.h>
#include <inttypes.h>

/* Instrumented lfs_getattr: logs parameters and results to /tmp/logs/207.txt
   Preserves original logic and calls through to lfs_getattr_. Does not dereference
   the user-supplied path (only logs the pointer) to avoid introducing extra
   out-of-bounds reads. */

lfs_ssize_t lfs_getattr(lfs_t *lfs, const char *path,
        uint8_t type, void *buffer, lfs_size_t size) {
    FILE *f = fopen("/tmp/logs/207.txt", "a");
    if (f != NULL) {
        /* Log entry: function entry and parameter state. We purposely do not
           inspect the contents of 'path' here (no dereference) to avoid
           creating further undefined behavior; we only log the pointer and
           numeric parameters so callers/test harnesses can correlate inputs. */
        fprintf(f, "ENTER lfs_getattr: lfs=%p path=%p type=%" PRIu8
                " buffer=%p size=%" PRIu32 "\n",
                (void*)lfs, (const void*)path, type, buffer,
                (uint32_t)size);
        fclose(f);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log error early return */
        f = fopen("/tmp/logs/207.txt", "a");
        if (f != NULL) {
            fprintf(f, "lfs_getattr: LFS_LOCK failed -> %d\n", err);
            fclose(f);
        }
        return err;
    }

    /* Call the original internal implementation. Note: lfs_getattr_ will
       eventually call lfs_dir_find which uses strspn/strcspn/memcmp on the
       provided 'path' pointer. If 'path' does not point to a NUL-terminated
       character array within its valid bounds, those calls may read beyond
       the bounds of the object referenced by 'path'. */
    lfs_ssize_t res = lfs_getattr_(lfs, path, type, buffer, size);

    f = fopen("/tmp/logs/207.txt", "a");
    if (f != NULL) {
        fprintf(f, "EXIT  lfs_getattr -> %" PRId32 "\n", res);
        /* Add a diagnostic note about the possible violation scenario for
           offline analysis correlated with the input pointer logged above. */
        fprintf(f, "DIAG: lfs_getattr called lfs_getattr_ which uses strspn/strcspn/memcmp
if path is not NUL-terminated this may cause out-of-bounds reads.\n");
        fclose(f);
    }

    LFS_UNLOCK(lfs->cfg);
    return res;
}
