#include <stdio.h>
#include <stddef.h>

/* Minimal local definition so the snippet is independently compilable.
   In the real project lfs_t is defined elsewhere; this mirrors the
   member used by the original code (cfg) so the call LFS_LOCK(lfs->cfg)
   compiles. */
typedef struct lfs { void *cfg; } lfs_t;

/* Prototype of the real helper; defined elsewhere in the project. */
int lfs_mkdir_(lfs_t *lfs, const char *path);

/* Preserves original function logic but adds probes that log the control
   path and state to /tmp/logs/225.txt. This demonstrates the unreachable
   early-return branch that occurs because LFS_LOCK always yields 0 in
   the provided project macros. */
int lfs_mkdir(lfs_t *lfs, const char *path) {
    FILE *log = fopen("/tmp/logs/225.txt", "a");
    if (log != NULL) {
        (void)fprintf(log, "ENTER lfs_mkdir: lfs=%p path=\"%s\"\n", (void*)lfs, path ? path : "(null)");
    }

    int err = LFS_LOCK(lfs->cfg);
    if (log != NULL) {
        (void)fprintf(log, "after LFS_LOCK: err=%d\n", err);
    }

    /* Original check retained. In the project this branch is unreachable
       because LFS_LOCK is defined to always return 0, demonstrating the
       Rule 2.1 violation. */
    if (err) {
        if (log != NULL) {
            (void)fprintf(log, "taking early return due to err=%d (UNREACHABLE under current macros)\n", err);
            (void)fclose(log);
        }
        return err;
    }

    if (log != NULL) {
        (void)fprintf(log, "calling lfs_mkdir_\n");
    }

    LFS_TRACE("lfs_mkdir(%p, \"%s\")", (void*)lfs, path);

    err = lfs_mkdir_(lfs, path);

    if (log != NULL) {
        (void)fprintf(log, "lfs_mkdir_ returned %d\n", err);
    }

    LFS_TRACE("lfs_mkdir -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (log != NULL) {
        (void)fprintf(log, "LFS_UNLOCK done, returning %d\n", err);
        (void)fclose(log);
    }

    return err;
}
