    probe_log("STATE", "Computed microSecPerSec=%lu (MicroSecPerTick=%ld, TicksPerSecond=%ld)",
              (unsigned long)microSecPerSec, (long)OS_SharedGlobalVars.MicroSecPerTick,
              (long)OS_SharedGlobalVars.TicksPerSecond);

    if (microSecPerSec != 1000000)
    {
        probe_log("BEFORE_OS_DEBUG", "About to call OS_DEBUG for microSecPerSec mismatch: %lu != 1000000", (unsigned long)microSecPerSec);
        OS_DEBUG("Warning: Microsecs per sec value of %lu does not equal 1000000 (MicroSecPerTick: %ld   "
                 "TicksPerSecond: %ld)\n",
                 (unsigned long)microSecPerSec, (long)OS_SharedGlobalVars.MicroSecPerTick,
                 (long)OS_SharedGlobalVars.TicksPerSecond);
        probe_log("AFTER_OS_DEBUG", "Returned from OS_DEBUG (if defined) after microSecPerSec warning");
    }

    if (return_code != OS_SUCCESS)
