void OS_BSP_Initialize(void)
{
    FILE *              fp;
    FILE *              logfp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;

    /* Open probe log file (append) to capture control flow and state */
    logfp = fopen("/tmp/logs/145.txt", "a");
    if (logfp)
    {
        fprintf(logfp, "OS_BSP_Initialize: entry\n");
        fprintf(logfp, "  effective uid = %u\n", (unsigned)geteuid());
        fflush(logfp);
    }

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     *
     * This special file represents the max depth of a POSIX message queue for an unprivileged user.
     *
     * In order to facilitate running in simulation mode without any need for root access --
     * this will allow the OSAL to successfully create message queues by truncating anything larger than this size.
     *
     * No need to check _LINUX_OS_ here; if the file fails to open, i.e. if not on Linux and the file does not exist,
     * then leave well enough alone and don't do anything.
     */
    if (geteuid() != 0)
    {
        if (logfp)
        {
            fprintf(logfp, "  running as non-root, attempting to open /proc/sys/fs/mqueue/msg_max\n");
            fflush(logfp);
        }

        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                if (logfp)
                {
                    fprintf(logfp, "  read from /proc/sys/fs/mqueue/msg_max: '%s'\n", buffer);
                    fflush(logfp);
                }

                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(strtoul(buffer, NULL, 10));
                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);

                if (logfp)
                {
                    fprintf(logfp, "  set OS_BSP_Global.MaxQueueDepth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
                    fflush(logfp);
                }
            }
            fclose(fp);
        }
        else
        {
            if (logfp)
            {
                fprintf(logfp, "  could not open /proc/sys/fs/mqueue/msg_max\n");
                fflush(logfp);
            }
        }
    }

    /* Initialize the low level access mutex (w/priority inheritance) */
    if (logfp)
    {
        fprintf(logfp, "Calling pthread_mutexattr_init()\n");
        fflush(logfp);
    }
    status = pthread_mutexattr_init(&mutex_attr);
    if (logfp)
    {
        fprintf(logfp, "  pthread_mutexattr_init returned status=%d (0 means success)\n", status);
        fprintf(logfp, "  (branch 'status < 0' would be %s)\n", (status < 0) ? "TAKEN" : "NOT_TAKEN");
        fflush(logfp);
    }
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));
    }

    if (logfp)
    {
        fprintf(logfp, "Calling pthread_mutexattr_setprotocol(..., PTHREAD_PRIO_INHERIT)\n");
        fflush(logfp);
    }
    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    if (logfp)
    {
        fprintf(logfp, "  pthread_mutexattr_setprotocol returned status=%d\n", status);
        fprintf(logfp, "  (branch 'status < 0' would be %s)\n", (status < 0) ? "TAKEN" : "NOT_TAKEN");
        /* Log a human-readable strerror only when status is non-zero (positive error codes) */
        if (status != 0)
        {
            fprintf(logfp, "  strerror(status) -> %s\n", strerror(status));
        }
        fflush(logfp);
    }
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
    }

    if (logfp)
    {
        fprintf(logfp, "Calling pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr)\n");
        fflush(logfp);
    }
    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    if (logfp)
    {
        fprintf(logfp, "  pthread_mutex_init returned status=%d\n", status);
        fprintf(logfp, "  (branch 'status < 0' would be %s)\n", (status < 0) ? "TAKEN" : "NOT_TAKEN");
        if (status != 0)
        {
            fprintf(logfp, "  strerror(status) -> %s\n", strerror(status));
        }
        fflush(logfp);
    }
    if (status < 0)
    {
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
    }

    if (logfp)
    {
        fprintf(logfp, "OS_BSP_Initialize: exit\n");
        fclose(logfp);
    }
}
