void OS_BSP_Initialize(void)
{
    FILE *              fp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;

    /* Probe log file to capture control flow and state for analysis of BSP_DEBUG usage */
    FILE *log_fp = fopen("/tmp/logs/146.txt", "a");
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: entered\n");
    }

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     *
     * This special file represents the max depth of a POSIX message queue for an unprivileged user.
     *
     * In order to facilitate running in simulation mode without any need for root access --
     * this will allow the OSAL to successfully create message queues by truncating anything larger than this size.
     *
     * No need to check _LINUX_OS_ here; if the file fails to open, i.e. if not on Linux and the file does not exist,
     * then leave well enough alone and don't do anything.
     */
    if (geteuid() != 0)
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: running as non-root (geteuid() != 0)\n");
        }

        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                unsigned long parsed = strtoul(buffer, NULL, 10);
                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(parsed);

                /* Record the path and the value that would be reported by BSP_DEBUG */
                if (log_fp)
                {
                    fprintf(log_fp, "OS_BSP_Initialize: read msg_max = %lu, set MaxQueueDepth = %u\n",
                            parsed, (unsigned int)OS_BSP_Global.MaxQueueDepth);
                    fprintf(log_fp, "OS_BSP_Initialize: BSP_DEBUG would be called with: \"Maximum user msg queue depth = %%u\\n\", %u\n",
                            (unsigned int)OS_BSP_Global.MaxQueueDepth);
                }

                /* Original BSP_DEBUG call retained to preserve logic (may be defined empty) */
                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
            }
            if (fclose(fp) != 0)
            {
                if (log_fp)
                {
                    fprintf(log_fp, "OS_BSP_Initialize: fclose(/proc/sys/fs/mqueue/msg_max) failed\n");
                }
            }
        }
        else
        {
            if (log_fp)
            {
                fprintf(log_fp, "OS_BSP_Initialize: could not open /proc/sys/fs/mqueue/msg_max (not present or inaccessible)\n");
            }
        }
    }
    else
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: running as root (geteuid() == 0) - skipping msg_max check\n");
        }
    }

    /* Initialize the low level access mutex (w/priority inheritance) */
    status = pthread_mutexattr_init(&mutex_attr);
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: pthread_mutexattr_init returned %d\n", status);
    }
    if (status < 0)
    {
        /* Log intended BSP_DEBUG invocation (which may be a no-op) */
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: BSP_DEBUG would be called with: \"pthread_mutexattr_init: %%s\\n\", strerror(status) (errno-like value: %d)\n",
                    status);
        }
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));
    }

    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: pthread_mutexattr_setprotocol returned %d\n", status);
    }
    if (status < 0)
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: BSP_DEBUG would be called with: \"pthread_mutexattr_setprotocol: %%s\\n\", strerror(status) (errno-like value: %d)\n",
                    status);
        }
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
    }

    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: pthread_mutex_init returned %d\n", status);
    }
    if (status < 0)
    {
        if (log_fp)
        {
            fprintf(log_fp, "OS_BSP_Initialize: BSP_DEBUG would be called with: \"pthread_mutex_init: %%s\\n\", strerror(status) (errno-like value: %d)\n",
                    status);
        }
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
    }

    if (log_fp)
    {
        fprintf(log_fp, "OS_BSP_Initialize: exiting\n");
        fclose(log_fp);
    }
}
