#include <stdio.h>
#include <string.h>
#include <stdint.h>
#include <stdbool.h>
#include <stddef.h>

/* Probe helper: append a single log entry describing the state. */
static void OS_FileSys_FindVirtMountPoint_log(const char *stage,
                                              const void *ref_ptr,
                                              const char *target_str,
                                              const void *filesys_ptr,
                                              unsigned long flags_val,
                                              size_t mplen,
                                              size_t vsize,
                                              int cmp_result,
                                              int final_decision)
{
    FILE *f = fopen("/tmp/logs/267.txt", "a");
    if (f == NULL)
    {
        return;
    }

    /* Print a compact, machine-readable single-line entry. */
    fprintf(f, "stage=%s ref=%p target_present=%s target_head=\"",
            stage,
            ref_ptr,
            (target_str != NULL) ? "yes" : "no");

    if (target_str != NULL)
    {
        /* Print up to 48 printable chars from the target (escape newlines). */
        size_t i;
        for (i = 0; i < 48 && target_str[i] != '\0'; ++i)
        {
            char c = target_str[i];
            if (c == '\n')
                fputs("\\n", f);
            else if (c == '\r')
                fputs("\\r", f);
            else if (c >= 32 && c < 127)
                fputc(c, f);
            else
                fputs("?", f);
        }
        if (i == 48 && target_str[i] != '\0')
            fputs("...", f);
    }
    fputs("\" ", f);

    fprintf(f, "filesys=%p flags=0x%lx mplen=%zu vsize=%zu cmp=%d decision=%d\n",
            filesys_ptr,
            flags_val,
            mplen,
            vsize,
            cmp_result,
            final_decision);

    fclose(f);
}

bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    /* Log initial state: we've cast ref to a const char * (target). */
    OS_FileSys_FindVirtMountPoint_log("enter", ref, target, filesys,
                                      (unsigned long)filesys->flags,
                                      0, /* mplen not yet computed */
                                      sizeof(filesys->virtual_mountpt),
                                      0, /* cmp_result not yet computed */
                                      -1 /* decision unknown */);

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        OS_FileSys_FindVirtMountPoint_log("not_mounted", ref, target, filesys,
                                          (unsigned long)filesys->flags,
                                          0,
                                          sizeof(filesys->virtual_mountpt),
                                          0,
                                          0);
        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    /*
     * The virtual_mountpt member should be a substring of the search target.
     * If this matches a basic substring check then it may be match
     */
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) ||
        strncmp(target, filesys->virtual_mountpt, mplen) != 0)
    {
        /* not a substring, so not a match */
        int cmp_result = 0;
        if (target != NULL)
        {
            /* record the comparison result for logging (safe replica) */
            cmp_result = strncmp(target, filesys->virtual_mountpt, mplen);
        }

        OS_FileSys_FindVirtMountPoint_log("no_substring", ref, target, filesys,
                                          (unsigned long)filesys->flags,
                                          mplen,
                                          sizeof(filesys->virtual_mountpt),
                                          cmp_result,
                                          0);
        return false;
    }

    /*
     * Confirm that the substring ends at either a directory separator
     * or the end of string  (so exact mount points also match).
     *
     * For instance consider a virtual_mountpt of /mnt/abc and searching
     * for target=/mnt/abcd - this should return false in that case.
     */
    int decision = (target[mplen] == '/' || target[mplen] == 0) ? 1 : 0;

    OS_FileSys_FindVirtMountPoint_log("final_check", ref, target, filesys,
                                      (unsigned long)filesys->flags,
                                      mplen,
                                      sizeof(filesys->virtual_mountpt),
                                      0,
                                      decision);

    return decision != 0;
}
