#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>
#include <stdbool.h>

/* Probe helper: append a single-line log to /tmp/logs/267.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/267.txt", "a");
    if (f == NULL)
    {
        /* If logging can't be opened, silently continue to avoid changing behavior */
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputc('\n', f);
    fclose(f);
}

bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;

    probe_log("OS_FileSys_FindVirtMountPoint ENTRY: ref=%p, token=%p, obj=%p", ref, (const void *)token, (const void *)obj);
    probe_log("target pointer=%p, target string=\"%s\"", (const void *)target, (target != NULL) ? target : "(null)");

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);
    probe_log("Lookup filesys record: filesys=%p", (const void *)filesys);

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        probe_log("filesys not virtual-mounted: flags=0x%lx", (unsigned long)filesys->flags);
        probe_log("OS_FileSys_FindVirtMountPoint EXIT: result=false (not virtual mounted)");
        return false;
    }

    probe_log("filesys is virtual-mounted: flags=0x%lx, virtual_mountpt=\"%s\"", (unsigned long)filesys->flags, filesys->virtual_mountpt);

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
    probe_log("computed mplen=%zu (sizeof virtual_mountpt=%zu)", mplen, sizeof(filesys->virtual_mountpt));

    /*
     * The virtual_mountpt member should be a substring of the search target.
     * If this matches a basic substring check then it may be match
     */
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) ||
        strncmp(target, filesys->virtual_mountpt, mplen) != 0)
    {
        int cmp = strncmp(target, filesys->virtual_mountpt, mplen);
        probe_log("substring check failed: mplen==0?%d, mplen>=sizeof?%d, strncmp-result=%d", (int)(mplen == 0), (int)(mplen >= sizeof(filesys->virtual_mountpt)), cmp);
        probe_log("OS_FileSys_FindVirtMountPoint EXIT: result=false (not a substring match)");
        return false;
    }

    /*
     * Confirm that the substring ends at either a directory separator
     * or the end of string  (so exact mount points also match).
     *
     * For instance consider a virtual_mountpt of /mnt/abc and searching
     * for target=/mnt/abcd - this should return false in that case.
     */
    bool result = (target[mplen] == '/' || target[mplen] == 0);
    probe_log("boundary check: target[%zu]=%d ('%c'), result=%d", mplen, (int)target[mplen], (target[mplen] != 0) ? target[mplen] : '0', (int)result);
    probe_log("OS_FileSys_FindVirtMountPoint EXIT: result=%d", (int)result);

    return result;
}
