#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Simple logging helper that appends human-readable records to /tmp/logs/250.txt
 * This preserves the original logic and only adds observational side-effects. */
static void OS_FileSys_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/250.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_FileSys_Initialize(char *address, const char *fsdevname, const char *fsvolname, size_t blocksize,
                            osal_blockcount_t numblocks, bool should_format)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;

    OS_FileSys_Log("OS_FileSys_Initialize ENTRY: fsdevname='%s' fsvolname='%s' address=%p blocksize=%zu numblocks=%llu should_format=%d",
                   fsdevname ? fsdevname : "(null)", fsvolname ? fsvolname : "(null)", (void *)address, blocksize,
                   (unsigned long long)numblocks, (int)should_format);

    /*
     * Check parameters
     *
     * Note "address" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_STRING(fsdevname, sizeof(filesys->device_name), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_STRING(fsvolname, sizeof(filesys->volume_name), OS_FS_ERR_PATH_TOO_LONG);

    /* check names are not empty strings */
    if (fsdevname[0] == 0 || fsvolname[0] == 0)
    {
        OS_FileSys_Log("OS_FileSys_Initialize PARAM ERROR: empty fsdevname or fsvolname");
        return OS_FS_ERR_PATH_INVALID;
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, fsdevname, &token);
    OS_FileSys_Log("OS_ObjectIdAllocateNew returned %ld token=%llu", (long)return_code, (unsigned long long)token);

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, fsdevname);

        /* populate the VolumeName and BlockSize ahead of the Impl call,
         * so the implementation can reference this info if necessary */
        filesys->blocksize = blocksize;
        filesys->numblocks = numblocks;
        filesys->address   = address;
        strncpy(filesys->volume_name, fsvolname, sizeof(filesys->volume_name) - 1);

        OS_FileSys_Log("After populate: blocksize=%zu numblocks=%llu address=%p volume_name='%s' fstype=%d flags=0x%08x",
                       filesys->blocksize, (unsigned long long)filesys->numblocks, (void *)filesys->address,
                       filesys->volume_name, (int)filesys->fstype, (unsigned)filesys->flags);

        /*
         * Determine basic type of filesystem, if not already known
         *
         * if either an address was supplied, or if the volume name
         * contains the string "RAM" then it is a RAM disk. Otherwise
         * leave the type as UNKNOWN and let the implementation decide.
         */
        if (filesys->fstype == OS_FILESYS_TYPE_UNKNOWN &&
            (filesys->address != NULL || strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                                 sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0))
        {
            filesys->fstype = OS_FILESYS_TYPE_VOLATILE_DISK;
            OS_FileSys_Log("Inferred fstype=OS_FILESYS_TYPE_VOLATILE_DISK for volume '%s'", filesys->volume_name);
        }

        return_code = OS_FileSysStartVolume_Impl(&token);
        OS_FileSys_Log("OS_FileSysStartVolume_Impl returned %ld for token=%llu", (long)return_code, (unsigned long long)token);

        if (return_code == OS_SUCCESS)
        {
            /*
             * The "mkfs" call also formats the device.
             * this is the primary difference between mkfs and initfs.
             */
            if (should_format)
            {
                unsigned int flags_before = filesys->flags;
                OS_FileSys_Log("About to call OS_FileSysFormatVolume_Impl token=%llu flags_before=0x%08x",
                               (unsigned long long)token, (unsigned)flags_before);

                return_code = OS_FileSysFormatVolume_Impl(&token);

                unsigned int flags_after = filesys->flags;
                OS_FileSys_Log("OS_FileSysFormatVolume_Impl returned %ld token=%llu flags_after=0x%08x",
                               (long)return_code, (unsigned long long)token, (unsigned)flags_after);

                /* Log if no observable change happened to filesys flags (evidence of no-op) */
                if (flags_before == flags_after)
                {
                    OS_FileSys_Log("NOTICE: No change in filesys->flags detected across FormatVolume call (possible no-op)");
                }
            }

            if (return_code == OS_SUCCESS)
            {
                filesys->flags |= OS_FILESYS_FLAG_IS_READY;
                OS_FileSys_Log("Marked volume ready: flags=0x%08x", (unsigned)filesys->flags);
            }
            else
            {
                /*
                 * To avoid leaving in an intermediate state,
                 * this also stops the volume if formatting failed.
                 * Cast to void to repress analysis warnings for
                 * ignored return value.
                 */
                OS_FileSys_Log("Formatting failed (code=%ld); calling OS_FileSysStopVolume_Impl token=%llu", (long)return_code,
                               (unsigned long long)token);
                (void)OS_FileSysStopVolume_Impl(&token);
            }
        }

        /* Check result, finalize record, and unlock global table. */
        OS_FileSys_Log("Calling OS_ObjectIdFinalizeNew with return_code=%ld token=%llu", (long)return_code, (unsigned long long)token);
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, NULL);
        OS_FileSys_Log("OS_ObjectIdFinalizeNew returned %ld", (long)return_code);
    }

    OS_FileSys_Log("OS_FileSys_Initialize EXIT return_code=%ld", (long)return_code);
    return return_code;
}
